import React, { useState } from "react";
import {
  Dialog, DialogTitle, DialogContent, DialogActions,
  TextField, Button, Grid, MenuItem, Avatar, IconButton, Typography
} from "@mui/material";
import { LinkedIn, Twitter, Save, Cancel } from "@mui/icons-material";

// Champs par défaut :
const types = ["Organisation", "Personnalité"];
const priorities = ["Haute", "Moyenne", "Basse"];
const statuses = ["À contacter", "En contact", "En attente", "Terminé"];

export default function ContactForm({ open, onClose, onSave, initial = {} }) {
  const [fields, setFields] = useState({
    name: initial.name || "",
    type: initial.type || "",
    priority: initial.priority || "Basse",
    status: initial.status || "À contacter",
    email: initial.email || "",
    phone: initial.phone || "",
    website: initial.website || "",
    address: initial.address || "",
    photo: initial.photo || "",
    notes: initial.notes || "",
    socials: initial.socials ? JSON.parse(initial.socials) : { linkedin: "", twitter: "" },
    notes_publiques: initial.notes_publiques || "",
    notes_privees: initial.notes_privees || "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setFields(f => ({ ...f, [name]: value }));
  }

  function handleSocialChange(e) {
    const { name, value } = e.target;
    setFields(f => ({ ...f, socials: { ...f.socials, [name]: value } }));
  }

  function handleSubmit(e) {
    e.preventDefault();
    // Remettre socials en string
    onSave({ ...fields, socials: JSON.stringify(fields.socials) });
  }

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>{initial.id ? "Modifier un contact" : "Ajouter un contact"}</DialogTitle>
      <DialogContent>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2} sx={{ pt: 1 }}>
            <Grid item xs={12} md={2} align="center">
              <Avatar src={fields.photo} sx={{ width: 64, height: 64, margin: "auto" }}>
                {fields.name ? fields.name[0] : "?"}
              </Avatar>
              <TextField
                label="Photo (URL)"
                name="photo"
                value={fields.photo}
                onChange={handleChange}
                fullWidth
                margin="dense"
                size="small"
                sx={{ mt: 1 }}
              />
            </Grid>
            <Grid item xs={12} md={10}>
              <TextField
                label="Nom"
                name="name"
                value={fields.name}
                onChange={handleChange}
                required
                fullWidth
                margin="dense"
                autoFocus
              />
              <TextField
                select
                label="Type"
                name="type"
                value={fields.type}
                onChange={handleChange}
                fullWidth
                margin="dense"
                sx={{ mr: 1 }}
              >
                {types.map(t => <MenuItem key={t} value={t}>{t}</MenuItem>)}
              </TextField>
              <TextField
                select
                label="Priorité"
                name="priority"
                value={fields.priority}
                onChange={handleChange}
                fullWidth
                margin="dense"
                sx={{ mr: 1 }}
              >
                {priorities.map(p => <MenuItem key={p} value={p}>{p}</MenuItem>)}
              </TextField>
              <TextField
                select
                label="Statut"
                name="status"
                value={fields.status}
                onChange={handleChange}
                fullWidth
                margin="dense"
              >
                {statuses.map(s => <MenuItem key={s} value={s}>{s}</MenuItem>)}
              </TextField>
              <Grid container spacing={1} sx={{ mt: 0 }}>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="Email"
                    name="email"
                    value={fields.email}
                    onChange={handleChange}
                    fullWidth
                    margin="dense"
                    type="email"
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    label="Téléphone"
                    name="phone"
                    value={fields.phone}
                    onChange={handleChange}
                    fullWidth
                    margin="dense"
                    type="tel"
                  />
                </Grid>
              </Grid>
              <TextField
                label="Adresse"
                name="address"
                value={fields.address}
                onChange={handleChange}
                fullWidth
                margin="dense"
              />
              <TextField
                label="Site web"
                name="website"
                value={fields.website}
                onChange={handleChange}
                fullWidth
                margin="dense"
                type="url"
              />
              <Grid container spacing={1} alignItems="center" sx={{ mt: 1 }}>
                <Grid item>
                  <LinkedIn fontSize="small" />
                </Grid>
                <Grid item xs>
                  <TextField
                    label="LinkedIn"
                    name="linkedin"
                    value={fields.socials.linkedin}
                    onChange={handleSocialChange}
                    fullWidth
                    margin="dense"
                  />
                </Grid>
                <Grid item>
                  <Twitter fontSize="small" />
                </Grid>
                <Grid item xs>
                  <TextField
                    label="Twitter"
                    name="twitter"
                    value={fields.socials.twitter}
                    onChange={handleSocialChange}
                    fullWidth
                    margin="dense"
                  />
                </Grid>
              </Grid>
              <TextField
                label="Notes publiques"
                name="notes_publiques"
                value={fields.notes_publiques}
                onChange={handleChange}
                fullWidth
                margin="dense"
                multiline
                minRows={2}
              />
              <TextField
                label="Notes privées"
                name="notes_privees"
                value={fields.notes_privees}
                onChange={handleChange}
                fullWidth
                margin="dense"
                multiline
                minRows={2}
                helperText="Visible uniquement pour l'équipe interne"
              />
            </Grid>
          </Grid>
        </form>
      </DialogContent>
      <DialogActions>
        <Button startIcon={<Cancel />} onClick={onClose}>Annuler</Button>
        <Button
          variant="contained"
          startIcon={<Save />}
          onClick={handleSubmit}
        >
          {initial.id ? "Enregistrer" : "Ajouter"}
        </Button>
      </DialogActions>
    </Dialog>
  );
}
