import React, { useState } from "react";
import {
  Box, Stack, Typography, Button, Paper, MenuItem,
  Select, FormControl, InputLabel, IconButton, TextField, Chip, Tooltip
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import InlineEdit from "./InlineEdit";

// Contrastes AAA
const priorityColors = {
  "Haute": "#b00020",
  "Moyenne": "#bf8600",
  "Basse": "#296d25"
};
const statusOptions = ["À faire", "En cours", "En attente", "Terminé"];

export default function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete, currentUser }) {
  const [filterStatus, setFilterStatus] = useState("");
  const [filterAssigned, setFilterAssigned] = useState("");
  const [search, setSearch] = useState("");

  // --- Actions
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) onAdd({
      title,
      priority: "Moyenne",
      status: "À faire",
      notes: "",
      assignedTo: null,
    });
  };
  const canEdit = (task) =>
    task.userId === currentUser.id || task.assignedTo === currentUser.id || currentUser.username === "admin";
  const canDelete = (task) =>
    task.userId === currentUser.id || currentUser.username === "admin";

  // --- Filtrage + recherche
  let filtered = tasks;
  if (filterStatus) filtered = filtered.filter(t => t.status === filterStatus);
  if (filterAssigned) filtered = filtered.filter(t => String(t.assignedTo) === filterAssigned);
  if (search.trim()) filtered = filtered.filter(t => t.title.toLowerCase().includes(search.trim().toLowerCase()));

  return (
    <Box sx={{ mb: 6 }}>
      <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 2, flexWrap: "wrap" }}>
        <Typography variant="h4" sx={{ flexGrow: 1, fontWeight: 700 }}>Tâches</Typography>
        <Button variant="contained" color="primary" size="large" onClick={addTask}>
          + Nouvelle
        </Button>
      </Stack>
      {/* Filtres */}
      <Stack direction="row" spacing={2} sx={{ mb: 2, flexWrap: "wrap" }}>
        <TextField
          placeholder="Recherche titre…"
          value={search}
          onChange={e => setSearch(e.target.value)}
          size="small"
          sx={{ minWidth: 180 }}
        />
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Statut</InputLabel>
          <Select
            value={filterStatus}
            label="Statut"
            onChange={e => setFilterStatus(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {statusOptions.map(status => <MenuItem key={status} value={status}>{status}</MenuItem>)}
          </Select>
        </FormControl>
        <FormControl size="small" sx={{ minWidth: 140 }}>
          <InputLabel>Assigné à</InputLabel>
          <Select
            value={filterAssigned}
            label="Assigné à"
            onChange={e => setFilterAssigned(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {users.map(u =>
              <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
            )}
          </Select>
        </FormControl>
        <Button variant="outlined" onClick={() => { setFilterStatus(""); setFilterAssigned(""); setSearch(""); }}>
          RÉINITIALISER
        </Button>
      </Stack>
      {/* Liste */}
      <Stack spacing={3}>
        {filtered.map(t => (
          <Paper
            key={t.id}
            elevation={2}
            sx={{
              px: { xs: 2, md: 3 }, py: 2, borderRadius: 3,
              border: `2.5px solid ${priorityColors[t.priority]}`,
              display: "flex", alignItems: "flex-start", gap: 2,
              background: "#fff"
            }}
          >
            <Box sx={{ flex: 1, minWidth: 0 }}>
              <Typography variant="h6" sx={{ fontWeight: 700, mb: 0.5 }}>
                <InlineEdit
                  value={t.title}
                  onSave={val => canEdit(t) && onUpdate(t.id, { ...t, title: val })}
                  disabled={!canEdit(t)}
                  style={{ fontWeight: 700, fontSize: "1.15rem", color: "#333" }}
                />
              </Typography>
              <Stack direction="row" spacing={2} sx={{ mb: 1 }}>
                <FormControl size="small" sx={{ minWidth: 130 }}>
                  <InputLabel>Statut</InputLabel>
                  <Select
                    value={t.status}
                    label="Statut"
                    onChange={e => canEdit(t) && onUpdate(t.id, { ...t, status: e.target.value })}
                    disabled={!canEdit(t)}
                  >
                    {statusOptions.map(status =>
                      <MenuItem key={status} value={status}>{status}</MenuItem>
                    )}
                  </Select>
                </FormControl>
                <FormControl size="small" sx={{ minWidth: 130 }}>
                  <InputLabel>Priorité</InputLabel>
                  <Select
                    value={t.priority}
                    label="Priorité"
                    onChange={e => canEdit(t) && onUpdate(t.id, { ...t, priority: e.target.value })}
                    disabled={!canEdit(t)}
                  >
                    {Object.entries(priorityColors).map(([k, color]) =>
                      <MenuItem key={k} value={k} sx={{ color, fontWeight: 700 }}>{k}</MenuItem>
                    )}
                  </Select>
                </FormControl>
                <FormControl size="small" sx={{ minWidth: 130 }}>
                  <InputLabel>Assigné à</InputLabel>
                  <Select
                    value={t.assignedTo || ""}
                    label="Assigné à"
                    onChange={e => canEdit(t) && onUpdate(t.id, { ...t, assignedTo: e.target.value ? Number(e.target.value) : null })}
                    disabled={!canEdit(t)}
                  >
                    <MenuItem value="">Non assigné</MenuItem>
                    {users.map(u =>
                      <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
                    )}
                  </Select>
                </FormControl>
              </Stack>
              <Typography variant="body2" color="text.secondary" sx={{ fontWeight: 500 }}>Notes :</Typography>
              <InlineEdit
                value={t.notes}
                onSave={val => canEdit(t) && onUpdate(t.id, { ...t, notes: val })}
                type="textarea"
                style={{
                  width: "100%",
                  minHeight: 36,
                  background: "#f8f8f8",
                  borderRadius: 5,
                  color: "#1a1a1a",
                  fontSize: "1rem"
                }}
                disabled={!canEdit(t)}
              />
            </Box>
            {canDelete(t) &&
              <Tooltip title="Supprimer la tâche">
                <IconButton aria-label="Supprimer" color="error" onClick={() => onDelete(t.id)} sx={{ ml: 1, mt: 0.5 }}>
                  <DeleteIcon fontSize="medium" />
                </IconButton>
              </Tooltip>
            }
          </Paper>
        ))}
        {!filtered.length &&
          <Typography sx={{ color: "#888", mt: 3 }}>Aucune tâche trouvée…</Typography>
        }
      </Stack>
    </Box>
  );
}
