import React, { useState } from "react";
import {
  Button, Box, TextField, MenuItem, Typography, Dialog,
  DialogTitle, DialogContent, DialogActions, IconButton, List, ListItem, ListItemText
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

export default function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete }) {
  // UI State
  const [filterStatus, setFilterStatus] = useState("");
  const [filterAssigned, setFilterAssigned] = useState("");
  const [search, setSearch] = useState("");
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editing, setEditing] = useState(null);

  // Filtrage/recherche
  let filtered = tasks;
  if (filterStatus) filtered = filtered.filter(t => t.status === filterStatus);
  if (filterAssigned) filtered = filtered.filter(t => String(t.assignedTo) === filterAssigned);
  if (search.trim()) filtered = filtered.filter(t =>
    t.title.toLowerCase().includes(search.trim().toLowerCase())
  );

  // Ajout/édition via modal
  const openDialog = (task) => { setEditing(task || { title: "", priority: "Moyenne", status: "À faire", notes: "", assignedTo: "" }); setDialogOpen(true); };
  const closeDialog = () => { setEditing(null); setDialogOpen(false); };
  const saveDialog = () => {
    if (editing.id) onUpdate(editing.id, { ...editing, assignedTo: editing.assignedTo ? Number(editing.assignedTo) : null });
    else onAdd({ ...editing, assignedTo: editing.assignedTo ? Number(editing.assignedTo) : null });
    closeDialog();
  };

  return (
    <Box sx={{ mb: 6 }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Tâches</Typography>
      <Box sx={{ display: "flex", gap: 2, mb: 2, flexWrap: "wrap" }}>
        <TextField
          size="small"
          label="Rechercher"
          variant="outlined"
          value={search}
          onChange={e => setSearch(e.target.value)}
          sx={{ minWidth: 180 }}
        />
        <TextField
          size="small"
          select
          label="Statut"
          value={filterStatus}
          onChange={e => setFilterStatus(e.target.value)}
          sx={{ minWidth: 150 }}
        >
          <MenuItem value="">Tous statuts</MenuItem>
          <MenuItem value="À faire">À faire</MenuItem>
          <MenuItem value="En cours">En cours</MenuItem>
          <MenuItem value="En attente">En attente</MenuItem>
          <MenuItem value="Terminé">Terminé</MenuItem>
        </TextField>
        <TextField
          size="small"
          select
          label="Assigné à"
          value={filterAssigned}
          onChange={e => setFilterAssigned(e.target.value)}
          sx={{ minWidth: 150 }}
        >
          <MenuItem value="">Tous assignés</MenuItem>
          {users.map(u =>
            <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
          )}
        </TextField>
        <Button variant="contained" onClick={() => openDialog(null)}>
          Ajouter une tâche
        </Button>
        <Button variant="text" color="secondary" onClick={() => { setFilterStatus(""); setFilterAssigned(""); setSearch(""); }}>
          Réinitialiser
        </Button>
      </Box>

      <List>
        {filtered.map(t => (
          <ListItem
            key={t.id}
            sx={{
              border: "1px solid #eee",
              borderRadius: 2,
              mb: 1,
              bgcolor: "#fff",
              boxShadow: 1,
              alignItems: "flex-start"
            }}
            secondaryAction={
              <IconButton edge="end" color="error" onClick={() => onDelete(t.id)}>
                <DeleteIcon />
              </IconButton>
            }
            onClick={() => openDialog(t)}
            style={{ cursor: "pointer" }}
          >
            <ListItemText
              primary={
                <Box>
                  <b>{t.title}</b>
                  <span style={{ color: priorityColor[t.priority], fontWeight: 'bold', marginLeft: 10 }}>
                    {t.priority}
                  </span>
                  <span style={{ marginLeft: 10, fontStyle: "italic", color: "#888" }}>{t.status}</span>
                </Box>
              }
              secondary={
                <>
                  <div>
                    Assigné à : <b>{users.find(u => u.id === t.assignedTo)?.username || "—"}</b>
                  </div>
                  {t.notes && <div style={{ color: "#888" }}>Notes: {t.notes}</div>}
                </>
              }
            />
          </ListItem>
        ))}
      </List>
      {!filtered.length && <div style={{ color: "#999", marginTop: 20 }}>Aucune tâche trouvée…</div>}

      {/* Modal Ajout/Édition */}
      <Dialog open={dialogOpen} onClose={closeDialog}>
        <DialogTitle>{editing?.id ? "Modifier la tâche" : "Nouvelle tâche"}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Titre"
            fullWidth
            value={editing?.title || ''}
            onChange={e => setEditing(edit => ({ ...edit, title: e.target.value }))}
          />
          <TextField
            margin="dense"
            label="Notes"
            fullWidth
            value={editing?.notes || ''}
            onChange={e => setEditing(edit => ({ ...edit, notes: e.target.value }))}
          />
          <TextField
            margin="dense"
            label="Statut"
            select
            fullWidth
            value={editing?.status || 'À faire'}
            onChange={e => setEditing(edit => ({ ...edit, status: e.target.value }))}
            sx={{ mt: 2 }}
          >
            <MenuItem value="À faire">À faire</MenuItem>
            <MenuItem value="En cours">En cours</MenuItem>
            <MenuItem value="En attente">En attente</MenuItem>
            <MenuItem value="Terminé">Terminé</MenuItem>
          </TextField>
          <TextField
            margin="dense"
            label="Priorité"
            select
            fullWidth
            value={editing?.priority || 'Moyenne'}
            onChange={e => setEditing(edit => ({ ...edit, priority: e.target.value }))}
            sx={{ mt: 2 }}
          >
            <MenuItem value="Haute">Haute</MenuItem>
            <MenuItem value="Moyenne">Moyenne</MenuItem>
            <MenuItem value="Basse">Basse</MenuItem>
          </TextField>
          <TextField
            margin="dense"
            label="Assigner à"
            select
            fullWidth
            value={editing?.assignedTo || ""}
            onChange={e => setEditing(edit => ({ ...edit, assignedTo: e.target.value }))}
            sx={{ mt: 2 }}
          >
            <MenuItem value="">Non assigné</MenuItem>
            {users.map(u =>
              <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
            )}
          </TextField>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeDialog}>Annuler</Button>
          <Button variant="contained" onClick={saveDialog}>
            {editing?.id ? "Enregistrer" : "Ajouter"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
