import React from 'react';
import LinearProgress from '@mui/material/LinearProgress';

function ProgressDashboard({ tasks }) {
  const total = tasks.length;
  const done = tasks.filter(t => t.status === 'Termin√©').length;
  const inProgress = tasks.filter(t => t.status === 'En cours').length;
  const todo = tasks.filter(t => t.status === '√Ä faire').length;
  const blocked = tasks.filter(t => t.status === 'Bloqu√©').length;
  const percent = total ? Math.round((done / total) * 100) : 0;

  return (
    <div style={{
      border: "1px solid #2196f3",
      padding: 16,
      borderRadius: 12,
      marginBottom: 16,
      background: "#f7fbff"
    }}>
      <h2>Statistiques du projet</h2>
      <div>
        <b>Total t√¢ches‚ÄØ:</b> {total}<br />
        <b>Termin√©es‚ÄØ:</b> {done} <span style={{ color: "green" }}>‚úîÔ∏è</span><br />
        <b>En cours‚ÄØ:</b> {inProgress} <span style={{ color: "orange" }}>‚è≥</span><br />
        <b>√Ä faire‚ÄØ:</b> {todo} <span style={{ color: "gray" }}>üïì</span><br />
        <b>Bloqu√©es‚ÄØ:</b> {blocked} <span style={{ color: "red" }}>‚õî</span><br />
      </div>
      <div style={{ marginTop: 16 }}>
        <b>Progression globale‚ÄØ: {percent}%</b>
        <LinearProgress variant="determinate" value={percent} style={{ height: 10, borderRadius: 5, marginTop: 8 }} />
      </div>
    </div>
  );
}

export default ProgressDashboard;
