import React from "react";

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };
const [filter, setFilter] = useState('');
const [statusFilter, setStatusFilter] = useState('Tous');

const filteredTasks = tasks.filter(t =>
  (statusFilter === 'Tous' || t.status === statusFilter) &&
  t.title.toLowerCase().includes(filter.toLowerCase())
);


function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete }) {
  // --- AJOUT ---
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: 'Moyenne',
        status: 'À faire',
        notes: '',
        assignedTo: null // Par défaut personne
      });
    }
  };

  // --- MODIF ---
  const updateTask = (id, field, value) => {
    const t = tasks.find(t => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
  };

  // --- SUPPRESSION ---
  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
    }
  };

  // Helper pour afficher le nom du responsable
  const getAssigneeName = (id) => {
    if (!id) return "";
    const u = users.find(u => u.id === id);
    return u ? u.username : "";
  };

  return (
    <div>
      <h2>Tâches</h2>
      <button onClick={addTask}>Ajouter une tâche</button>
      <ul>
        {tasks.map(t => (
          <li key={t.id} style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}>
            <b>{t.title}</b>
            <span style={{
              color: priorityColor[t.priority],
              fontWeight: 'bold',
              marginLeft: 10
            }}>{t.priority}</span>
            <br />
            Statut :
            <select
              value={t.status}
              onChange={e => updateTask(t.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À faire</option>
              <option>En cours</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Priorité :
            <select
              value={t.priority}
              onChange={e => updateTask(t.id, "priority", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>Haute</option>
              <option>Moyenne</option>
              <option>Basse</option>
            </select>
            <br />
            Notes :
            <textarea
              value={t.notes}
              onChange={e => updateTask(t.id, "notes", e.target.value)}
              style={{ width: "95%", minHeight: 30 }}
            />
            <br />
            Assigner à&nbsp;:
            <select
              value={t.assignedTo || ""}
              onChange={e => updateTask(t.id, "assignedTo", e.target.value ? Number(e.target.value) : null)}
              style={{ marginLeft: 5 }}
            >
              <option value="">-- Personne --</option>
              {users.map(u =>
                <option key={u.id} value={u.id}>{u.username}</option>
              )}
            </select>
            {t.assignedTo && (
              <span style={{ marginLeft: 8, color: "#0077cc" }}>
                ({getAssigneeName(t.assignedTo)})
              </span>
            )}
            <br />
            <button onClick={() => removeTask(t.id)} style={{ color: "red" }}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskBoard;
