import React from 'react';

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

function ContactBoard({ contacts, setContacts }) {
  // --- AJOUT ---
  const addContact = async () => {
    const name = prompt("Nom du contact ?");
    if (name) {
      // POST /contacts => ajoute sur le serveur
      const resp = await fetch('http://localhost:4000/contacts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name,
          type: 'Organisation', // ou 'Personnalité'
          priority: 'Basse',
          status: 'À contacter',
          notes: ''
        })
      });
      const newContact = await resp.json();
      setContacts([...contacts, newContact]); // Met à jour localement
    }
  };

  // --- MODIF (pour select, textarea, etc) ---
  const updateContact = async (id, field, value) => {
    const contact = contacts.find(c => c.id === id);
    if (!contact) return;
    const updated = { ...contact, [field]: value };
    // PUT /contacts/:id => met à jour sur le serveur
    await fetch(`http://localhost:4000/contacts/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ [field]: value })
    });
    // Mets à jour la liste côté front
    setContacts(contacts.map(c => c.id === id ? updated : c));
  };

  // --- SUPPRESSION ---
  const removeContact = async (id) => {
    if (window.confirm("Supprimer ce contact ?")) {
      // DELETE /contacts/:id => supprime sur le serveur
      await fetch(`http://localhost:4000/contacts/${id}`, {
        method: 'DELETE'
      });
      setContacts(contacts.filter(c => c.id !== id));
    }
  };

  return (
    <div>
      <h2>Contacts</h2>
      <button onClick={addContact}>Ajouter un contact</button>
      <ul>
        {contacts.map(c => (
          <li key={c.id} style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}>
            <b>{c.name}</b> ({c.type})
            <span style={{
              color: priorityColor[c.priority],
              fontWeight: 'bold',
              marginLeft: 10
            }}>{c.priority}</span>
            <br />
            Statut : 
            <select
              value={c.status}
              onChange={e => updateContact(c.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À contacter</option>
              <option>En contact</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Priorité : 
            <select
              value={c.priority}
              onChange={e => updateContact(c.id, "priority", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>Haute</option>
              <option>Moyenne</option>
              <option>Basse</option>
            </select>
            <br />
            Notes : 
            <textarea
              value={c.notes}
              onChange={e => updateContact(c.id, "notes", e.target.value)}
              style={{ width: "95%", minHeight: 30 }}
            />
            <button onClick={() => removeContact(c.id)} style={{ color: "red" }}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ContactBoard;
