import React, { useState } from "react";
import {
  Stack, Button, Box, Typography, Select, MenuItem,
  InputLabel, FormControl, Snackbar, IconButton, Paper, TextField, Tooltip
} from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';
import InlineEdit from "./InlineEdit";

const priorityColor = {
  'Haute': '#b00020',
  'Moyenne': '#bf8600',
  'Basse': '#296d25'
};
const typeOptions = ["Organisation", "Personnalité"];
const statusOptions = [
  "À contacter", "En contact", "En attente", "Terminé"
];

export default function ContactBoard({ contacts, onAdd, onUpdate, onDelete, currentUser }) {
  const [filterStatus, setFilterStatus] = useState("");
  const [filterType, setFilterType] = useState("");
  const [search, setSearch] = useState("");
  const [snackbar, setSnackbar] = useState({ open: false, msg: "" });

  const canEdit = (c) =>
    c.userId === currentUser.id || currentUser.username === "admin";
  const canDelete = canEdit;

  // Ajout
  const addContact = () => {
    const name = prompt("Nom du contact ?");
    if (name) {
      onAdd({
        name,
        type: 'Organisation',
        priority: 'Basse',
        status: 'À contacter',
        notes: ''
      });
      setSnackbar({ open: true, msg: "Contact ajouté !" });
    }
  };

  // Modif
  const updateContact = (id, field, value) => {
    const c = contacts.find(c => c.id === id);
    if (!c) return;
    onUpdate(id, { ...c, [field]: value });
    setSnackbar({ open: true, msg: "Contact mis à jour !" });
  };

  // Suppression
  const removeContact = (id) => {
    if (window.confirm("Supprimer ce contact ?")) {
      onDelete(id);
      setSnackbar({ open: true, msg: "Contact supprimé." });
    }
  };

  // Filtrage/recherche
  let filtered = contacts;
  if (filterStatus) filtered = filtered.filter(c => c.status === filterStatus);
  if (filterType) filtered = filtered.filter(c => c.type === filterType);
  if (search.trim())
    filtered = filtered.filter(c =>
      c.name.toLowerCase().includes(search.trim().toLowerCase())
    );

  return (
    <Box sx={{ maxWidth: 780, mx: "auto", p: 2, mt: 5 }}>
      <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 2, flexWrap: "wrap" }}>
        <Typography variant="h4" fontWeight={700} sx={{ flexGrow: 1, letterSpacing: "-0.5px" }}>
          Contacts
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={addContact}
          sx={{ fontWeight: 600, minWidth: 120, height: 44, fontSize: 16, boxShadow: "none" }}
        >
          + Nouveau
        </Button>
      </Stack>
      {/* Barre de filtres */}
      <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 2, flexWrap: "wrap" }}>
        <TextField
          placeholder="Recherche nom…"
          value={search}
          onChange={e => setSearch(e.target.value)}
          size="small"
          sx={{ minWidth: 180 }}
          inputProps={{ style: { fontSize: 15, letterSpacing: "-0.2px" } }}
        />
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Statut</InputLabel>
          <Select
            value={filterStatus}
            label="Statut"
            onChange={e => setFilterStatus(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {statusOptions.map(status =>
              <MenuItem key={status} value={status}>{status}</MenuItem>
            )}
          </Select>
        </FormControl>
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Type</InputLabel>
          <Select
            value={filterType}
            label="Type"
            onChange={e => setFilterType(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {typeOptions.map(type =>
              <MenuItem key={type} value={type}>{type}</MenuItem>
            )}
          </Select>
        </FormControl>
        <Button variant="outlined" onClick={() => { setFilterStatus(""); setFilterType(""); setSearch(""); }}>
          RÉINITIALISER
        </Button>
      </Stack>
      {/* Liste des contacts */}
      <Stack spacing={3} sx={{ mt: 2 }}>
        {filtered.map(c => (
          <Paper
            key={c.id}
            elevation={2}
            sx={{
              borderRadius: 3,
              boxShadow: "0 1px 6px 0 rgba(0,0,0,0.07)",
              border: `2.5px solid ${priorityColor[c.priority]}`,
              px: 3, py: 2,
              display: "flex", alignItems: "flex-start", gap: 2,
              background: "#fff"
            }}
          >
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" fontWeight={600} sx={{ mb: 0.2, display: "flex", alignItems: "center" }}>
                <InlineEdit
                  value={c.name}
                  onSave={val => canEdit(c) && updateContact(c.id, "name", val)}
                  disabled={!canEdit(c)}
                  style={{ fontWeight: 600, fontSize: "1.08rem", minWidth: 100, color: "#000" }}
                />
                <Typography component="span" sx={{ fontWeight: 500, color: "#444", fontSize: 14, ml: 1, letterSpacing: "-0.1px" }}>
                  ({c.type})
                </Typography>
              </Typography>
              <Stack direction="row" spacing={2} alignItems="center" sx={{ mt: 0.5, mb: 0.5 }}>
                <FormControl size="small" sx={{ minWidth: 110 }}>
                  <InputLabel>Statut</InputLabel>
                  <Select
                    value={c.status}
                    label="Statut"
                    onChange={e => canEdit(c) && updateContact(c.id, "status", e.target.value)}
                    disabled={!canEdit(c)}
                  >
                    {statusOptions.map(status =>
                      <MenuItem key={status} value={status}>{status}</MenuItem>
                    )}
                  </Select>
                </FormControl>
                <FormControl size="small" sx={{ minWidth: 110 }}>
                  <InputLabel>Priorité</InputLabel>
                  <Select
                    value={c.priority}
                    label="Priorité"
                    onChange={e => canEdit(c) && updateContact(c.id, "priority", e.target.value)}
                    disabled={!canEdit(c)}
                    sx={{ color: priorityColor[c.priority], fontWeight: "bold" }}
                  >
                    <MenuItem value="Haute" sx={{ color: priorityColor['Haute'] }}>Haute</MenuItem>
                    <MenuItem value="Moyenne" sx={{ color: priorityColor['Moyenne'] }}>Moyenne</MenuItem>
                    <MenuItem value="Basse" sx={{ color: priorityColor['Basse'] }}>Basse</MenuItem>
                  </Select>
                </FormControl>
              </Stack>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5, mb: 0.3, fontWeight: 500, letterSpacing: "-0.1px" }}>
                Notes&nbsp;:
              </Typography>
              <InlineEdit
                value={c.notes}
                onSave={val => canEdit(c) && updateContact(c.id, "notes", val)}
                type="textarea"
                style={{
                  width: "100%",
                  minHeight: 32,
                  background: "#f9f9f9",
                  borderRadius: 4,
                  color: "#111",
                  fontSize: "1rem"
                }}
                disabled={!canEdit(c)}
              />
            </Box>
            {canDelete(c) &&
              <Tooltip title="Supprimer le contact">
                <IconButton
                  aria-label="Supprimer"
                  color="error"
                  onClick={() => removeContact(c.id)}
                  sx={{ ml: 2, width: 44, height: 44 }}
                >
                  <DeleteIcon fontSize="medium" />
                </IconButton>
              </Tooltip>
            }
          </Paper>
        ))}
        {!filtered.length &&
          <Typography sx={{ color: "#888", mt: 3 }}>Aucun contact trouvé…</Typography>
        }
      </Stack>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={1800}
        onClose={() => setSnackbar({ open: false, msg: "" })}
        message={snackbar.msg}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
      />
    </Box>
  );
}
