// src/components/ContactBoard.js

import React, { useState } from "react";
import InlineEdit from "./InlineEdit";
import {
  Card, CardContent, Typography, Button, Select, MenuItem, Box,
  TextField, InputLabel, FormControl, Snackbar, IconButton, Stack
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };
const typeOptions = ["Organisation", "Personnalité"];

function ContactBoard({ contacts, onAdd, onUpdate, onDelete }) {
  // --- UI State ---
  const [filterStatus, setFilterStatus] = useState("");
  const [filterType, setFilterType] = useState("");
  const [search, setSearch] = useState("");
  const [snackbar, setSnackbar] = useState({ open: false, msg: "" });

  // --- AJOUT ---
  const addContact = () => {
    const name = prompt("Nom du contact ?");
    if (name) {
      onAdd({
        name,
        type: 'Organisation',
        priority: 'Basse',
        status: 'À contacter',
        notes: ''
      });
      setSnackbar({ open: true, msg: "Contact ajouté !" });
    }
  };

  // --- MODIF ---
  const updateContact = (id, field, value) => {
    const c = contacts.find(c => c.id === id);
    if (!c) return;
    onUpdate(id, { ...c, [field]: value });
    setSnackbar({ open: true, msg: "Contact mis à jour !" });
  };

  // --- SUPPRESSION ---
  const removeContact = (id) => {
    if (window.confirm("Supprimer ce contact ?")) {
      onDelete(id);
      setSnackbar({ open: true, msg: "Contact supprimé." });
    }
  };

  // --- FILTRAGE + RECHERCHE ---
  let filtered = contacts;
  if (filterStatus) filtered = filtered.filter(c => c.status === filterStatus);
  if (filterType) filtered = filtered.filter(c => c.type === filterType);
  if (search.trim())
    filtered = filtered.filter(c =>
      c.name.toLowerCase().includes(search.trim().toLowerCase())
    );

  return (
    <Box>
      <Stack direction="row" spacing={2} alignItems="center" sx={{ my: 2 }}>
        <Typography variant="h5">Contacts</Typography>
        <Button variant="contained" color="primary" onClick={addContact}>
          Ajouter un contact
        </Button>
      </Stack>

      {/* Filtres */}
      <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 2, flexWrap: "wrap" }}>
        <TextField
          placeholder="Rechercher un nom…"
          value={search}
          onChange={e => setSearch(e.target.value)}
          size="small"
        />
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Statut</InputLabel>
          <Select
            value={filterStatus}
            label="Statut"
            onChange={e => setFilterStatus(e.target.value)}
          >
            <MenuItem value="">— Statut —</MenuItem>
            <MenuItem value="À contacter">À contacter</MenuItem>
            <MenuItem value="En contact">En contact</MenuItem>
            <MenuItem value="En attente">En attente</MenuItem>
            <MenuItem value="Terminé">Terminé</MenuItem>
          </Select>
        </FormControl>
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Type</InputLabel>
          <Select
            value={filterType}
            label="Type"
            onChange={e => setFilterType(e.target.value)}
          >
            <MenuItem value="">— Type —</MenuItem>
            {typeOptions.map(type =>
              <MenuItem key={type} value={type}>{type}</MenuItem>
            )}
          </Select>
        </FormControl>
        <Button onClick={() => { setFilterStatus(""); setFilterType(""); setSearch(""); }}>
          Réinitialiser
        </Button>
      </Stack>

      <Stack spacing={2}>
        {filtered.map(c => (
          <Card key={c.id} variant="outlined" sx={{ borderLeft: `4px solid ${priorityColor[c.priority]}` }}>
            <CardContent>
              <Stack direction="row" justifyContent="space-between" alignItems="center">
                <Box>
                  <Typography variant="h6" sx={{ mb: 1 }}>
                    <InlineEdit
                      value={c.name}
                      onSave={val => updateContact(c.id, "name", val)}
                    />
                    <Typography component="span" sx={{ fontSize: 14, color: "#555", ml: 1 }}>
                      ({c.type})
                    </Typography>
                  </Typography>
                  <FormControl size="small" sx={{ mr: 2, minWidth: 120 }}>
                    <InputLabel>Type</InputLabel>
                    <Select
                      value={c.type}
                      label="Type"
                      onChange={e => updateContact(c.id, "type", e.target.value)}
                    >
                      {typeOptions.map(type =>
                        <MenuItem key={type} value={type}>{type}</MenuItem>
                      )}
                    </Select>
                  </FormControl>
                  <FormControl size="small" sx={{ mr: 2, minWidth: 120 }}>
                    <InputLabel>Statut</InputLabel>
                    <Select
                      value={c.status}
                      label="Statut"
                      onChange={e => updateContact(c.id, "status", e.target.value)}
                    >
                      <MenuItem value="À contacter">À contacter</MenuItem>
                      <MenuItem value="En contact">En contact</MenuItem>
                      <MenuItem value="En attente">En attente</MenuItem>
                      <MenuItem value="Terminé">Terminé</MenuItem>
                    </Select>
                  </FormControl>
                  <FormControl size="small" sx={{ minWidth: 120 }}>
                    <InputLabel>Priorité</InputLabel>
                    <Select
                      value={c.priority}
                      label="Priorité"
                      onChange={e => updateContact(c.id, "priority", e.target.value)}
                      sx={{ color: priorityColor[c.priority], fontWeight: "bold" }}
                    >
                      <MenuItem value="Haute" sx={{ color: "red" }}>Haute</MenuItem>
                      <MenuItem value="Moyenne" sx={{ color: "orange" }}>Moyenne</MenuItem>
                      <MenuItem value="Basse" sx={{ color: "green" }}>Basse</MenuItem>
                    </Select>
                  </FormControl>
                  <Box sx={{ mt: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      Notes :
                    </Typography>
                    <InlineEdit
                      value={c.notes}
                      onSave={val => updateContact(c.id, "notes", val)}
                      type="textarea"
                      style={{ width: "100%", minHeight: 30 }}
                    />
                  </Box>
                </Box>
                <IconButton aria-label="Supprimer" color="error" onClick={() => removeContact(c.id)}>
                  <DeleteIcon />
                </IconButton>
              </Stack>
            </CardContent>
          </Card>
        ))}
      </Stack>
      {!filtered.length &&
        <Typography sx={{ color: "#999", mt: 4 }}>Aucun contact trouvé…</Typography>
      }
      <Snackbar
        open={snackbar.open}
        autoHideDuration={1800}
        onClose={() => setSnackbar({ open: false, msg: "" })}
        message={snackbar.msg}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
      />
    </Box>
  );
}

export default ContactBoard;
