import React, { useState } from "react";

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

function TaskBoard({ tasks, onAdd, onUpdate, onDelete }) {
  // --- Filtres & Recherche ---
  const [filter, setFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('Tous');
  const [priorityFilter, setPriorityFilter] = useState('Toutes');

  // On filtre d'abord les tâches selon critères, puis on les affiche
  const filteredTasks = tasks.filter(t =>
    (statusFilter === 'Tous' || t.status === statusFilter) &&
    (priorityFilter === 'Toutes' || t.priority === priorityFilter) &&
    (t.title.toLowerCase().includes(filter.toLowerCase()))
  );

  // --- AJOUT ---
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: 'Moyenne',
        status: 'À faire',
        notes: ''
      });
    }
  };

  // --- MODIF ---
  const updateTask = (id, field, value) => {
    const t = tasks.find(t => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
  };

  // --- SUPPRESSION ---
  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
    }
  };

  return (
    <div>
      <h2>Tâches</h2>
      <button onClick={addTask}>Ajouter une tâche</button>
      <div style={{ margin: "10px 0" }}>
        <input
          placeholder="Recherche..."
          value={filter}
          onChange={e => setFilter(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <select value={statusFilter} onChange={e => setStatusFilter(e.target.value)} style={{ marginRight: 8 }}>
          <option value="Tous">Tous les statuts</option>
          <option value="À faire">À faire</option>
          <option value="En cours">En cours</option>
          <option value="En attente">En attente</option>
          <option value="Terminé">Terminé</option>
        </select>
        <select value={priorityFilter} onChange={e => setPriorityFilter(e.target.value)}>
          <option value="Toutes">Toutes priorités</option>
          <option value="Haute">Haute</option>
          <option value="Moyenne">Moyenne</option>
          <option value="Basse">Basse</option>
        </select>
      </div>
      <ul>
        {filteredTasks.map(t => (
          <li key={t.id} style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}>
            <b>{t.title}</b>
            <span style={{
              color: priorityColor[t.priority],
              fontWeight: 'bold',
              marginLeft: 10
            }}>{t.priority}</span>
            <br />
            Statut :
            <select
              value={t.status}
              onChange={e => updateTask(t.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À faire</option>
              <option>En cours</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Priorité :
            <select
              value={t.priority}
              onChange={e => updateTask(t.id, "priority", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>Haute</option>
              <option>Moyenne</option>
              <option>Basse</option>
            </select>
            <br />
            Notes :
            <textarea
              value={t.notes}
              onChange={e => updateTask(t.id, "notes", e.target.value)}
              style={{ width: "95%", minHeight: 30 }}
            />
            <button onClick={() => removeTask(t.id)} style={{ color: "red" }}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskBoard;
