import React from "react";
import InlineEdit from "./InlineEdit";

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

function TaskBoard({ tasks, onAdd, onUpdate, onDelete, users = [] }) {
  // --- AJOUT ---
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: 'Moyenne',
        status: 'À faire',
        notes: '',
        assignedTo: null,
      });
    }
  };

  // --- MODIF ---
  const updateTask = (id, field, value) => {
    const t = tasks.find(t => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
  };

  // --- SUPPRESSION ---
  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
    }
  };

  return (
    <div>
      <h2>Tâches</h2>
      <button onClick={addTask}>Ajouter une tâche</button>
      <ul>
        {tasks.map(t => (
          <li key={t.id} style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}>
            <b>
              <InlineEdit
                value={t.title}
                onSave={val => updateTask(t.id, "title", val)}
              />
            </b>
            <span style={{
              color: priorityColor[t.priority],
              fontWeight: 'bold',
              marginLeft: 10
            }}>
              <select
                value={t.priority}
                onChange={e => updateTask(t.id, "priority", e.target.value)}
                style={{ marginLeft: 5, color: priorityColor[t.priority], fontWeight: "bold" }}
              >
                <option>Haute</option>
                <option>Moyenne</option>
                <option>Basse</option>
              </select>
            </span>
            <br />
            Statut :
            <select
              value={t.status}
              onChange={e => updateTask(t.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À faire</option>
              <option>En cours</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Assigner à :
            <select
              value={t.assignedTo || ""}
              onChange={e => updateTask(t.id, "assignedTo", e.target.value ? Number(e.target.value) : null)}
              style={{ marginLeft: 5 }}
            >
              <option value="">Non assignée</option>
              {users.map(u =>
                <option key={u.id} value={u.id}>{u.username}</option>
              )}
            </select>
            <br />
            Notes :
            <InlineEdit
              value={t.notes}
              onSave={val => updateTask(t.id, "notes", val)}
              type="textarea"
              style={{ width: "95%", minHeight: 30 }}
            />
            <br />
            <button onClick={() => removeTask(t.id)} style={{ color: "red" }}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskBoard;
