import React, { useState } from "react";
import InlineEdit from "./InlineEdit";
import {
  Stack, Button, Box, Typography, Select, MenuItem,
  InputLabel, FormControl, Snackbar, IconButton, TextField
} from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';

// Couleurs contrastées
const priorityColor = {
  'Haute': '#b00020',    // Rouge foncé
  'Moyenne': '#bf8600',  // Orange foncé
  'Basse': '#296d25'     // Vert foncé
};
const statusOptions = [
  "À faire", "En cours", "En attente", "Terminé"
];

function TaskBoard({ tasks, users, user, onAdd, onUpdate, onDelete }) {
  const [filterStatus, setFilterStatus] = useState("");
  const [filterAssigned, setFilterAssigned] = useState("");
  const [search, setSearch] = useState("");
  const [snackbar, setSnackbar] = useState({ open: false, msg: "" });

  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: 'Moyenne',
        status: 'À faire',
        notes: '',
        assignedTo: null
      });
      setSnackbar({ open: true, msg: "Tâche ajoutée !" });
    }
  };

  const updateTask = (id, field, value) => {
    const t = tasks.find(t => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
    setSnackbar({ open: true, msg: "Tâche mise à jour !" });
  };

  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
      setSnackbar({ open: true, msg: "Tâche supprimée." });
    }
  };

  // Filtrage/recherche
  let filtered = tasks;
  if (filterStatus) filtered = filtered.filter(t => t.status === filterStatus);
  if (filterAssigned) filtered = filtered.filter(t => String(t.assignedTo) === filterAssigned);
  if (search.trim())
    filtered = filtered.filter(t =>
      t.title.toLowerCase().includes(search.trim().toLowerCase())
    );

  return (
    <Box sx={{ maxWidth: 800, mx: "auto", p: 2 }}>
      <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 2, flexWrap: "wrap" }}>
        <Typography variant="h4" fontWeight={700} sx={{ flexGrow: 1, letterSpacing: "-0.5px" }}>
          Tâches
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={addTask}
          sx={{ fontWeight: 600, minWidth: 120, height: 44, fontSize: 16, boxShadow: "none" }}
        >
          + Nouvelle
        </Button>
      </Stack>
      <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 2, flexWrap: "wrap" }}>
        <TextField
          placeholder="Recherche titre…"
          value={search}
          onChange={e => setSearch(e.target.value)}
          size="small"
          sx={{ minWidth: 180 }}
          inputProps={{ style: { fontSize: 15, letterSpacing: "-0.2px" } }}
        />
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Statut</InputLabel>
          <Select
            value={filterStatus}
            label="Statut"
            onChange={e => setFilterStatus(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {statusOptions.map(status =>
              <MenuItem key={status} value={status}>{status}</MenuItem>
            )}
          </Select>
        </FormControl>
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Assigné à</InputLabel>
          <Select
            value={filterAssigned}
            label="Assigné à"
            onChange={e => setFilterAssigned(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {users.map(u =>
              <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
            )}
          </Select>
        </FormControl>
        <Button variant="outlined" onClick={() => { setFilterStatus(""); setFilterAssigned(""); setSearch(""); }}>
          Réinitialiser
        </Button>
      </Stack>
      <Stack spacing={2} sx={{ mt: 2 }}>
        {filtered.map(t => (
          <Box
            key={t.id}
            sx={{
              borderRadius: 2,
              boxShadow: "0 1px 6px 0 rgba(0,0,0,0.06)",
              border: `2px solid ${priorityColor[t.priority]}`,
              px: 3, py: 2,
              display: "flex",
              alignItems: "flex-start",
              justifyContent: "space-between",
              gap: 2,
              background: "#fff"
            }}
          >
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" fontWeight={600} color="text.primary" sx={{ mb: 0.2 }}>
                <InlineEdit
                  value={t.title}
                  onSave={val => updateTask(t.id, "title", val)}
                  style={{ fontWeight: 600, fontSize: "1.08rem", minWidth: 100, color: "#000" }}
                />
              </Typography>
              <Stack direction="row" spacing={2} alignItems="center" sx={{ mt: 0.5, mb: 0.5 }}>
                <FormControl size="small" sx={{ minWidth: 110 }}>
                  <InputLabel>Statut</InputLabel>
                  <Select
                    value={t.status}
                    label="Statut"
                    onChange={e => updateTask(t.id, "status", e.target.value)}
                  >
                    {statusOptions.map(status =>
                      <MenuItem key={status} value={status}>{status}</MenuItem>
                    )}
                  </Select>
                </FormControl>
                <FormControl size="small" sx={{ minWidth: 110 }}>
                  <InputLabel>Priorité</InputLabel>
                  <Select
                    value={t.priority}
                    label="Priorité"
                    onChange={e => updateTask(t.id, "priority", e.target.value)}
                    sx={{
                      color: priorityColor[t.priority],
                      fontWeight: "bold"
                    }}
                  >
                    <MenuItem value="Haute" sx={{ color: priorityColor['Haute'] }}>Haute</MenuItem>
                    <MenuItem value="Moyenne" sx={{ color: priorityColor['Moyenne'] }}>Moyenne</MenuItem>
                    <MenuItem value="Basse" sx={{ color: priorityColor['Basse'] }}>Basse</MenuItem>
                  </Select>
                </FormControl>
                <FormControl size="small" sx={{ minWidth: 120 }}>
                  <InputLabel>Assigné à</InputLabel>
                  <Select
                    value={t.assignedTo || ""}
                    label="Assigné à"
                    onChange={e => updateTask(t.id, "assignedTo", e.target.value ? Number(e.target.value) : null)}
                  >
                    <MenuItem value="">Non assigné</MenuItem>
                    {users.map(u =>
                      <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
                    )}
                  </Select>
                </FormControl>
              </Stack>
              <Typography
                variant="body2"
                color="text.secondary"
                sx={{ mt: 0.5, mb: 0.3, fontWeight: 500, letterSpacing: "-0.1px" }}
              >
                Notes&nbsp;:
              </Typography>
              <InlineEdit
                value={t.notes}
                onSave={val => updateTask(t.id, "notes", val)}
                type="textarea"
                style={{
                  width: "100%",
                  minHeight: 32,
                  background: "#f9f9f9",
                  borderRadius: 4,
                  color: "#111",
                  fontSize: "1rem"
                }}
              />
            </Box>
            {/* Bouton de suppression visible SEULEMENT pour le créateur */}
            {t.userId === user.id && (
              <IconButton
                aria-label="Supprimer"
                color="error"
                onClick={() => removeTask(t.id)}
                sx={{ ml: 2, width: 44, height: 44 }}
              >
                <DeleteIcon fontSize="medium" />
              </IconButton>
            )}
          </Box>
        ))}
        {!filtered.length &&
          <Typography sx={{ color: "#888", mt: 3 }}>Aucune tâche trouvée…</Typography>
        }
      </Stack>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={1800}
        onClose={() => setSnackbar({ open: false, msg: "" })}
        message={snackbar.msg}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
      />
    </Box>
  );
}

export default TaskBoard;
