import React, { useEffect, useState } from "react";
import { Paper, Typography, Box, TextField, Button } from "@mui/material";
import axios from "axios";

function ThemeOfTheMonth({ user }) {
  const [theme, setTheme] = useState({ title: "", description: "" });
  const [edit, setEdit] = useState(false);
  const [form, setForm] = useState({ title: "", description: "" });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:4000/theme").then(res => {
      setTheme(res.data || {});
      setForm(res.data || {});
      setLoading(false);
    });
  }, []);

  const handleSave = async () => {
    await axios.put("http://localhost:4000/theme", form, {
      headers: { Authorization: "Bearer " + localStorage.getItem("token") }
    });
    setTheme(form);
    setEdit(false);
  };

  // Simple isAdmin : user?.username === "admin"
  const isAdmin = user && user.username === "admin";

  if (loading) return null;

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 3, background: "#fbeee6" }}>
      <Typography variant="h5" fontWeight="bold" sx={{ mb: 1 }}>
        {edit ? (
          <TextField
            label="Titre du thème"
            value={form.title}
            onChange={e => setForm(f => ({ ...f, title: e.target.value }))}
            fullWidth
            size="small"
            sx={{ fontWeight: "bold", mb: 1 }}
          />
        ) : (
          theme.title || "Aucun thème défini"
        )}
      </Typography>
      <Typography variant="body1" sx={{ mb: 2 }}>
        {edit ? (
          <TextField
            label="Description"
            value={form.description}
            onChange={e => setForm(f => ({ ...f, description: e.target.value }))}
            fullWidth
            multiline
            minRows={2}
            sx={{ mb: 1 }}
          />
        ) : (
          theme.description
        )}
      </Typography>
      {isAdmin && (
        <Box>
          {edit ? (
            <>
              <Button variant="contained" color="primary" size="small" onClick={handleSave}>Enregistrer</Button>
              <Button variant="outlined" size="small" sx={{ ml: 1 }} onClick={() => setEdit(false)}>Annuler</Button>
            </>
          ) : (
            <Button variant="contained" color="secondary" size="small" onClick={() => setEdit(true)}>
              Modifier le thème
            </Button>
          )}
        </Box>
      )}
    </Paper>
  );
}

export default ThemeOfTheMonth;
