import React, { useState } from "react";
import InlineEdit from "./InlineEdit";
import {
  Stack,
  Button,
  Box,
  Typography,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  IconButton,
  TextField,
  Chip
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import PriorityHighIcon from "@mui/icons-material/PriorityHigh";
import LowPriorityIcon from "@mui/icons-material/LowPriority";
import AssignmentTurnedInIcon from "@mui/icons-material/AssignmentTurnedIn";
import PersonIcon from "@mui/icons-material/Person";

const PRIORITIES = [
  { value: "Haute", label: "Haute", icon: <PriorityHighIcon color="error" fontSize="small" /> },
  { value: "Moyenne", label: "Moyenne", icon: <AssignmentTurnedInIcon color="warning" fontSize="small" /> },
  { value: "Basse", label: "Basse", icon: <LowPriorityIcon color="success" fontSize="small" /> }
];

const STATUSES = ["À faire", "En cours", "En attente", "Terminé"];

function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete }) {
  const [filterStatus, setFilterStatus] = useState("");
  const [filterAssigned, setFilterAssigned] = useState("");
  const [search, setSearch] = useState("");

  // --- Ajout ---
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: "Moyenne",
        status: "À faire",
        notes: "",
        assignedTo: null,
      });
    }
  };

  // --- Modif ---
  const updateTask = (id, field, value) => {
    const t = tasks.find((t) => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
  };

  // --- Suppression ---
  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
    }
  };

  // --- Filtrage + Recherche ---
  let filtered = tasks;
  if (filterStatus) {
    filtered = filtered.filter((t) => t.status === filterStatus);
  }
  if (filterAssigned) {
    filtered = filtered.filter((t) => String(t.assignedTo) === filterAssigned);
  }
  if (search.trim()) {
    filtered = filtered.filter((t) =>
      t.title.toLowerCase().includes(search.trim().toLowerCase())
    );
  }

  return (
    <Box sx={{ mt: 4 }}>
      <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 2 }}>
        <Typography variant="h5" sx={{ fontWeight: "bold" }}>
          Tâches
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={addTask}
          sx={{ minWidth: 150 }}
        >
          Ajouter une tâche
        </Button>
        <Box sx={{ flexGrow: 1 }} />
        <TextField
          label="Rechercher"
          value={search}
          onChange={e => setSearch(e.target.value)}
          size="small"
          sx={{ minWidth: 200, bgcolor: "#fff" }}
        />
        <FormControl size="small" sx={{ minWidth: 120 }}>
          <InputLabel id="status-label">Statut</InputLabel>
          <Select
            labelId="status-label"
            value={filterStatus}
            label="Statut"
            onChange={e => setFilterStatus(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {STATUSES.map(st => (
              <MenuItem key={st} value={st}>{st}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <FormControl size="small" sx={{ minWidth: 150 }}>
          <InputLabel id="assigned-label">Assigné à</InputLabel>
          <Select
            labelId="assigned-label"
            value={filterAssigned}
            label="Assigné à"
            onChange={e => setFilterAssigned(e.target.value)}
          >
            <MenuItem value="">Tous</MenuItem>
            {users.map(u => (
              <MenuItem key={u.id} value={u.id}>
                <PersonIcon sx={{ mr: 1 }} fontSize="small" /> {u.username}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        <Button
          variant="outlined"
          onClick={() => { setFilterStatus(""); setFilterAssigned(""); setSearch(""); }}
        >
          Réinitialiser
        </Button>
      </Stack>

      <Stack spacing={2}>
        {filtered.length === 0 && (
          <Typography color="text.secondary" align="center" sx={{ mt: 3 }}>
            Aucune tâche trouvée…
          </Typography>
        )}
        {filtered.map(t => (
          <Box
            key={t.id}
            sx={{
              p: 2,
              bgcolor: "#fafbfc",
              border: "1px solid #e0e0e0",
              borderRadius: 2,
              boxShadow: 1,
              display: "flex",
              flexDirection: "column",
              gap: 1.5,
              transition: "box-shadow 0.2s",
              "&:hover": { boxShadow: 3 }
            }}
          >
            <Stack direction="row" alignItems="center" spacing={2}>
              <InlineEdit
                value={t.title}
                onSave={val => updateTask(t.id, "title", val)}
                style={{ fontSize: "1.1rem", fontWeight: "bold", flex: 1, minWidth: 150 }}
              />

              <FormControl size="small" sx={{ minWidth: 100 }}>
                <InputLabel id={`priority-${t.id}`}>Priorité</InputLabel>
                <Select
                  labelId={`priority-${t.id}`}
                  value={t.priority}
                  label="Priorité"
                  onChange={e => updateTask(t.id, "priority", e.target.value)}
                  sx={{ fontWeight: "bold" }}
                >
                  {PRIORITIES.map(prio => (
                    <MenuItem key={prio.value} value={prio.value}>
                      <Stack direction="row" alignItems="center" spacing={1}>
                        {prio.icon}
                        <span>{prio.label}</span>
                      </Stack>
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>

              <FormControl size="small" sx={{ minWidth: 120 }}>
                <InputLabel id={`status-${t.id}`}>Statut</InputLabel>
                <Select
                  labelId={`status-${t.id}`}
                  value={t.status}
                  label="Statut"
                  onChange={e => updateTask(t.id, "status", e.target.value)}
                >
                  {STATUSES.map(st => (
                    <MenuItem key={st} value={st}>{st}</MenuItem>
                  ))}
                </Select>
              </FormControl>

              <FormControl size="small" sx={{ minWidth: 150 }}>
                <InputLabel id={`assignedTo-${t.id}`}>Assigné à</InputLabel>
                <Select
                  labelId={`assignedTo-${t.id}`}
                  value={t.assignedTo || ""}
                  label="Assigné à"
                  onChange={e => updateTask(t.id, "assignedTo", e.target.value ? Number(e.target.value) : null)}
                >
                  <MenuItem value="">Non assigné</MenuItem>
                  {users.map(u =>
                    <MenuItem key={u.id} value={u.id}>
                      <PersonIcon sx={{ mr: 1 }} fontSize="small" /> {u.username}
                    </MenuItem>
                  )}
                </Select>
              </FormControl>

              <IconButton
                onClick={() => removeTask(t.id)}
                size="large"
                color="error"
                aria-label="Supprimer la tâche"
                sx={{ ml: 2 }}
              >
                <DeleteIcon />
              </IconButton>
            </Stack>

            <Box>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5, fontWeight: 500 }}>
                Notes :
              </Typography>
              <InlineEdit
                value={t.notes}
                onSave={val => updateTask(t.id, "notes", val)}
                type="textarea"
                style={{ width: "100%", minHeight: 30, fontSize: "1rem" }}
              />
            </Box>
          </Box>
        ))}
      </Stack>
    </Box>
  );
}

export default TaskBoard;
