import React, { useState } from "react";
import InlineEdit from "./InlineEdit";
import { Button, List, ListItem, Typography, Box } from '@mui/material';

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete }) {
  // --- UI State ---
  const [filterStatus, setFilterStatus] = useState("");        // "" = all
  const [filterAssigned, setFilterAssigned] = useState("");    // "" = all
  const [search, setSearch] = useState("");

  // --- AJOUT ---
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: "Moyenne",
        status: "À faire",
        notes: "",
        assignedTo: null,
      });
    }
  };

  // --- MODIF ---
  const updateTask = (id, field, value) => {
    const t = tasks.find((t) => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
  };

  // --- SUPPRESSION ---
  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
    }
  };

  // --- FILTRAGE + RECHERCHE ---
  let filtered = tasks;
  if (filterStatus) {
    filtered = filtered.filter((t) => t.status === filterStatus);
  }
  if (filterAssigned) {
    filtered = filtered.filter((t) => String(t.assignedTo) === filterAssigned);
  }
  if (search.trim()) {
    filtered = filtered.filter((t) =>
      t.title.toLowerCase().includes(search.trim().toLowerCase())
    );
  }

  return (
    <div>
      <h2>
        Tâches
        <button onClick={addTask} style={{ marginLeft: 16 }}>Ajouter une tâche</button>
      </h2>
      {/* Filtres */}
      <div style={{ marginBottom: 12, display: "flex", gap: 10, flexWrap: "wrap" }}>
        <input
          placeholder="Rechercher dans les titres…"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ padding: 4, minWidth: 150 }}
        />
        <select
          value={filterStatus}
          onChange={e => setFilterStatus(e.target.value)}
        >
          <option value="">— Statut —</option>
          <option value="À faire">À faire</option>
          <option value="En cours">En cours</option>
          <option value="En attente">En attente</option>
          <option value="Terminé">Terminé</option>
        </select>
        <select
          value={filterAssigned}
          onChange={e => setFilterAssigned(e.target.value)}
        >
          <option value="">— Assigné à —</option>
          {users.map(u => (
            <option key={u.id} value={u.id}>{u.username}</option>
          ))}
        </select>
        <button onClick={() => { setFilterStatus(""); setFilterAssigned(""); setSearch(""); }}>Réinitialiser</button>
      </div>

      <ul>
        {filtered.map(t => (
          <li key={t.id} style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}>
            <b>
              <InlineEdit
                value={t.title}
                onSave={val => updateTask(t.id, "title", val)}
              />
            </b>
            <span style={{
              color: priorityColor[t.priority],
              fontWeight: 'bold',
              marginLeft: 10
            }}>
              <select
                value={t.priority}
                onChange={e => updateTask(t.id, "priority", e.target.value)}
                style={{ marginLeft: 5, color: priorityColor[t.priority], fontWeight: "bold" }}
              >
                <option>Haute</option>
                <option>Moyenne</option>
                <option>Basse</option>
              </select>
            </span>
            <br />
            Statut :
            <select
              value={t.status}
              onChange={e => updateTask(t.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À faire</option>
              <option>En cours</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Assigné à :
            <select
              value={t.assignedTo || ""}
              onChange={e => updateTask(t.id, "assignedTo", e.target.value ? Number(e.target.value) : null)}
              style={{ marginLeft: 5 }}
            >
              <option value="">Non assigné</option>
              {users.map(u =>
                <option key={u.id} value={u.id}>{u.username}</option>
              )}
            </select>
            <br />
            Notes :
            <InlineEdit
              value={t.notes}
              onSave={val => updateTask(t.id, "notes", val)}
              type="textarea"
              style={{ width: "95%", minHeight: 30 }}
            />
            <br />
            <button onClick={() => removeTask(t.id)} style={{ color: "red" }}>Supprimer</button>
          </li>
        ))}
      </ul>
      {!filtered.length && <div style={{ color: "#999", marginTop: 20 }}>Aucune tâche trouvée…</div>}
    </div>
  );
}

export default TaskBoard;
