import React from 'react';

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

function TaskBoard({ tasks, onUpdate }) {
  // --- AJOUT ---
  const addTask = async () => {
    const name = prompt("Nom de la tâche ?");
    if (name) {
      const newTasks = [
        ...tasks,
        {
          id: Date.now(), // Unicité simple, ou laisse gérer le backend
          name,
          priority: 'Moyenne',
          status: 'À faire',
          notes: ''
        }
      ];
      // POST la liste complète
      await fetch('http://localhost:4000/data', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tasks: newTasks })
      });
      onUpdate(newTasks);
    }
  };

  // --- MODIF (priorité, statut, notes, etc) ---
  const updateTask = async (id, field, value) => {
    const newTasks = tasks.map(t =>
      t.id === id ? { ...t, [field]: value } : t
    );
    await fetch('http://localhost:4000/data', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ tasks: newTasks })
    });
    onUpdate(newTasks);
  };

  // --- SUPPRESSION ---
  const removeTask = async (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      const newTasks = tasks.filter(t => t.id !== id);
      await fetch('http://localhost:4000/data', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tasks: newTasks })
      });
      onUpdate(newTasks);
    }
  };

  return (
    <div>
      <h2>Tâches</h2>
      <button onClick={addTask}>Ajouter une tâche</button>
      <ul>
        {tasks.map(t => (
          <li key={t.id} style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}>
            <b>{t.name}</b>
            <span style={{
              color: priorityColor[t.priority],
              fontWeight: 'bold',
              marginLeft: 10
            }}>{t.priority}</span>
            <br />
            Statut :
            <select
              value={t.status}
              onChange={e => updateTask(t.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À faire</option>
              <option>En cours</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Priorité :
            <select
              value={t.priority}
              onChange={e => updateTask(t.id, "priority", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>Haute</option>
              <option>Moyenne</option>
              <option>Basse</option>
            </select>
            <br />
            Notes :
            <textarea
              value={t.notes}
              onChange={e => updateTask(t.id, "notes", e.target.value)}
              style={{ width: "95%", minHeight: 30 }}
            />
            <button onClick={() => removeTask(t.id)} style={{ color: "red" }}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskBoard;
