// src/App.js
import React, { useState, useCallback, useEffect } from "react";
import axios from "axios";
import { ThemeProvider, CssBaseline, Box, AppBar, Toolbar, Typography, Button, Container, Paper, Tabs, Tab, Snackbar, Stack } from "@mui/material";
import ListAltIcon from '@mui/icons-material/ListAlt';
import PeopleAltIcon from '@mui/icons-material/PeopleAlt';
import SaveAltIcon from '@mui/icons-material/SaveAlt';
import CloudDownloadIcon from '@mui/icons-material/CloudDownload';
import LogoutIcon from '@mui/icons-material/Logout';
import ThemeOfTheMonth from "./components/ThemeOfTheMonth";
import TaskBoard from "./components/TaskBoard";
import ContactBoard from "./components/ContactBoard";
import ProgressDashboard from "./components/ProgressDashboard";
import theme from "./theme"; // voir section suivante

function getToken() { return localStorage.getItem('token'); }
function setToken(token) { localStorage.setItem('token', token); }
function removeToken() { localStorage.removeItem('token'); }
function authHeaders() { return { Authorization: "Bearer " + getToken() }; }

function AuthForm({ onLogin }) {
  const [mode, setMode] = useState("login");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");
  async function handleSubmit(e) {
    e.preventDefault();
    setErr("");
    try {
      if (mode === "register") {
        await axios.post("http://localhost:4000/register", { username, password });
        setMode("login");
        setErr("Compte créé. Connectez-vous !");
      } else {
        const res = await axios.post("http://localhost:4000/login", { username, password });
        setToken(res.data.token);
        await onLogin();
      }
    } catch (e) {
      setErr(e?.response?.data?.error || "Erreur serveur");
    }
  }
  return (
    <Container maxWidth="xs" sx={{ minHeight: "100vh", display: "flex", alignItems: "center" }}>
      <Paper sx={{ width: "100%", p: 4, borderRadius: 4 }}>
        <Typography variant="h5" fontWeight={700} align="center" mb={2}>
          {mode === "login" ? "Connexion" : "Inscription"}
        </Typography>
        <form onSubmit={handleSubmit}>
          <input placeholder="Nom d'utilisateur" value={username} onChange={e => setUsername(e.target.value)} required style={{ width: "100%", marginBottom: 12, padding: 10, fontSize: 16, borderRadius: 6, border: '1px solid #eaeaea' }} />
          <input placeholder="Mot de passe" value={password} onChange={e => setPassword(e.target.value)} required type="password" style={{ width: "100%", marginBottom: 12, padding: 10, fontSize: 16, borderRadius: 6, border: '1px solid #eaeaea' }} />
          <Button variant="contained" color="primary" type="submit" fullWidth sx={{ mb: 1, fontWeight: 600 }}>
            {mode === "login" ? "Se connecter" : "Créer un compte"}
          </Button>
        </form>
        <Button color="secondary" fullWidth size="small" onClick={() => setMode(mode === "login" ? "register" : "login")}>
          {mode === "login" ? "Créer un compte" : "Déjà inscrit ? Se connecter"}
        </Button>
        {err && <Typography color="error" align="center" sx={{ mt: 2 }}>{err}</Typography>}
      </Paper>
    </Container>
  );
}

export default function App() {
  const [user, setUser] = useState(null);
  const [page, setPage] = useState("loading");
  const [tasks, setTasks] = useState([]);
  const [contacts, setContacts] = useState([]);
  const [users, setUsers] = useState([]);
  const [githubStatus, setGithubStatus] = useState('');
  const [snack, setSnack] = useState('');
  const [tab, setTab] = useState(0);
  const [themeOfMonth, setThemeOfMonth] = useState(null);

  // Load everything
  const loadUserAndData = useCallback(async () => {
    const token = getToken();
    if (!token) {
      setUser(null);
      setPage("auth");
      setTasks([]); setContacts([]); setUsers([]);
      return;
    }
    try {
      const me = await axios.get("http://localhost:4000/me", { headers: authHeaders() });
      setUser(me.data);
      setPage("dashboard");
      const [tasksRes, contactsRes, usersRes, themeRes] = await Promise.all([
        axios.get("http://localhost:4000/tasks", { headers: authHeaders() }),
        axios.get("http://localhost:4000/contacts", { headers: authHeaders() }),
        axios.get("http://localhost:4000/users", { headers: authHeaders() }),
        axios.get("http://localhost:4000/theme"),
      ]);
      setTasks(tasksRes.data);
      setContacts(contactsRes.data);
      setUsers(usersRes.data);
      setThemeOfMonth(themeRes.data);
    } catch (e) {
      removeToken(); setUser(null); setPage("auth");
      setTasks([]); setContacts([]); setUsers([]);
    }
  }, []);
  useEffect(() => { loadUserAndData(); }, [loadUserAndData]);

  // CRUD
  const fetchTasks = async () => {
    const res = await axios.get("http://localhost:4000/tasks", { headers: authHeaders() });
    setTasks(res.data);
  };
  const fetchContacts = async () => {
    const res = await axios.get("http://localhost:4000/contacts", { headers: authHeaders() });
    setContacts(res.data);
  };
  const handleTaskAdd = async (task) => {
    await axios.post("http://localhost:4000/tasks", task, { headers: authHeaders() });
    fetchTasks();
    setSnack('Tâche ajoutée !');
  };
  const handleTaskUpdate = async (id, update) => {
    await axios.put(`http://localhost:4000/tasks/${id}`, update, { headers: authHeaders() });
    fetchTasks();
    setSnack('Tâche modifiée');
  };
  const handleTaskDelete = async (id) => {
    await axios.delete(`http://localhost:4000/tasks/${id}`, { headers: authHeaders() });
    fetchTasks();
    setSnack('Tâche supprimée');
  };
  const handleContactAdd = async (contact) => {
    await axios.post("http://localhost:4000/contacts", contact, { headers: authHeaders() });
    fetchContacts();
    setSnack('Contact ajouté !');
  };
  const handleContactUpdate = async (id, update) => {
    await axios.put(`http://localhost:4000/contacts/${id}`, update, { headers: authHeaders() });
    fetchContacts();
    setSnack('Contact modifié');
  };
  const handleContactDelete = async (id) => {
    await axios.delete(`http://localhost:4000/contacts/${id}`, { headers: authHeaders() });
    fetchContacts();
    setSnack('Contact supprimé');
  };

  async function saveToGitHub(setStatus) {
    setStatus("Envoi sur GitHub...");
    try {
      const res = await axios.post("http://localhost:4000/github-save", { tasks, contacts });
      setStatus(res.data.ok ? "Sauvegardé sur GitHub !" : "Erreur : fallback issue GitHub créé");
    } catch {
      setStatus("Erreur critique lors de la sauvegarde");
    }
  }
  function exportJSON() {
    const blob = new Blob([JSON.stringify({ tasks, contacts }, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url; a.download = "dashboard-export.json"; a.click();
    URL.revokeObjectURL(url);
  }
  function logout() {
    removeToken(); setUser(null); setPage("auth");
    setTasks([]); setContacts([]); setUsers([]);
  }

  if (page === "loading") return <div>Chargement…</div>;
  if (page === "auth") return <AuthForm onLogin={loadUserAndData} />;

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: "100vh",
          background: "linear-gradient(120deg, #2979ff 0%, #8f5cff 100%)",
          pb: 8
        }}
      >
        <AppBar position="static" color="primary" elevation={0} sx={{ background: "rgba(41,121,255,0.93)" }}>
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700 }}>
              Dashboard Collaboratif
            </Typography>
            <Typography sx={{ mr: 2 }}>Connecté en tant que <b>{user?.username}</b></Typography>
            <Button color="inherit" startIcon={<LogoutIcon />} onClick={logout}>
              Se déconnecter
            </Button>
          </Toolbar>
        </AppBar>
        <Container maxWidth="md" sx={{ mt: 4 }}>
          {/* Thème du mois (bandeau glassmorphique) */}
          {themeOfMonth && (
            <Paper elevation={4} sx={{
              p: 3,
              mb: 4,
              background: "rgba(255,240,230,0.92)",
              borderLeft: "8px solid #8f5cff",
              borderRadius: 3,
              boxShadow: "0 6px 40px rgba(70,50,110,0.10)"
            }}>
              <Typography variant="h5" fontWeight={800}>{themeOfMonth.title}</Typography>
              <Typography color="text.secondary" sx={{ mt: 1 }}>
                {themeOfMonth.description}
              </Typography>
            </Paper>
          )}

          {/* Dashboard/Stats (glasscard) */}
          <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
            <ProgressDashboard tasks={tasks} />
          </Paper>

          {/* Tabs Navigation */}
          <Tabs
            value={tab}
            onChange={(_, v) => setTab(v)}
            variant="fullWidth"
            sx={{
              mb: 3,
              borderRadius: 2,
              boxShadow: "0 2px 12px rgba(80,80,120,0.13)",
              background: "rgba(255,255,255,0.94)"
            }}
          >
            <Tab icon={<ListAltIcon />} iconPosition="start" label="Tâches" sx={{ fontWeight: 700 }} />
            <Tab icon={<PeopleAltIcon />} iconPosition="start" label="Contacts" sx={{ fontWeight: 700 }} />
          </Tabs>

          {/* Action buttons */}
          <Stack direction="row" spacing={2} mb={4} justifyContent="flex-end">
            <Button variant="contained" startIcon={<SaveAltIcon />} onClick={() => saveToGitHub(setGithubStatus)}>
              Sauvegarder sur GitHub
            </Button>
            <Button variant="outlined" startIcon={<CloudDownloadIcon />} onClick={exportJSON}>
              Exporter en JSON
            </Button>
            <Typography sx={{ ml: 2, color: "#888", alignSelf: "center" }}>{githubStatus}</Typography>
          </Stack>

          {/* Onglets */}
          {tab === 0 && (
            <TaskBoard
              tasks={tasks}
              users={users}
              onAdd={handleTaskAdd}
              onUpdate={handleTaskUpdate}
              onDelete={handleTaskDelete}
            />
          )}
          {tab === 1 && (
            <ContactBoard
              contacts={contacts}
              onAdd={handleContactAdd}
              onUpdate={handleContactUpdate}
              onDelete={handleContactDelete}
            />
          )}
        </Container>
        <Snackbar open={!!snack} autoHideDuration={2200} onClose={() => setSnack('')} message={snack} />
      </Box>
    </ThemeProvider>
  );
}
