import React, { useState } from "react";
import {
  Box, Paper, Stack, Typography, IconButton, Button, Select, MenuItem, FormControl, InputLabel, TextField, Tooltip, Snackbar
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import AssignmentTurnedInIcon from "@mui/icons-material/AssignmentTurnedIn";
import InlineEdit from "./InlineEdit";

const priorityColor = {
  'Haute': '#b00020',
  'Moyenne': '#bf8600',
  'Basse': '#296d25'
};

const statusOptions = ["À faire", "En cours", "En attente", "Terminé"];

export default function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete }) {
  const [filterStatus, setFilterStatus] = useState("");
  const [filterAssigned, setFilterAssigned] = useState("");
  const [search, setSearch] = useState("");
  const [snackbar, setSnackbar] = useState({ open: false, msg: "" });

  // Ajout
  const addTask = () => {
    const title = prompt("Nom de la tâche ?");
    if (title) {
      onAdd({
        title,
        priority: "Moyenne",
        status: "À faire",
        notes: "",
        assignedTo: null
      });
      setSnackbar({ open: true, msg: "Tâche ajoutée !" });
    }
  };
  // Modif
  const updateTask = (id, field, value) => {
    const t = tasks.find(t => t.id === id);
    if (!t) return;
    onUpdate(id, { ...t, [field]: value });
    setSnackbar({ open: true, msg: "Tâche mise à jour !" });
  };
  // Suppression
  const removeTask = (id) => {
    if (window.confirm("Supprimer cette tâche ?")) {
      onDelete(id);
      setSnackbar({ open: true, msg: "Tâche supprimée." });
    }
  };

  // Filtrage/recherche
  let filtered = tasks;
  if (filterStatus) filtered = filtered.filter(t => t.status === filterStatus);
  if (filterAssigned) filtered = filtered.filter(t => String(t.assignedTo) === filterAssigned);
  if (search.trim()) filtered = filtered.filter(t => t.title.toLowerCase().includes(search.trim().toLowerCase()));

  return (
    <Box sx={{ maxWidth: 800, mx: "auto", pb: 3 }}>
      <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 2 }}>
        <Typography variant="h4" fontWeight={700} sx={{ flexGrow: 1 }}>Tâches</Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={addTask}
          startIcon={<AddIcon />}
          sx={{ fontWeight: 600, minWidth: 120, height: 44, fontSize: 16, boxShadow: "none" }}
        >
          + Nouvelle
        </Button>
      </Stack>

      {/* Barre de filtres */}
      <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 2, flexWrap: "wrap" }}>
        <TextField
          placeholder="Recherche titre…"
          value={search}
          onChange={e => setSearch(e.target.value)}
          size="small"
          sx={{ minWidth: 170 }}
        />
        <FormControl size="small" sx={{ minWidth: 130 }}>
          <InputLabel>Statut</InputLabel>
          <Select value={filterStatus} label="Statut" onChange={e => setFilterStatus(e.target.value)}>
            <MenuItem value="">Tous</MenuItem>
            {statusOptions.map(status =>
              <MenuItem key={status} value={status}>{status}</MenuItem>
            )}
          </Select>
        </FormControl>
        <FormControl size="small" sx={{ minWidth: 140 }}>
          <InputLabel>Assigné à</InputLabel>
          <Select value={filterAssigned} label="Assigné à" onChange={e => setFilterAssigned(e.target.value)}>
            <MenuItem value="">Tous</MenuItem>
            {users.map(u =>
              <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
            )}
          </Select>
        </FormControl>
        <Button variant="outlined" onClick={() => { setFilterStatus(""); setFilterAssigned(""); setSearch(""); }}>
          Réinitialiser
        </Button>
      </Stack>

      {/* Liste des tâches */}
      <Stack spacing={3} sx={{ mt: 2 }}>
        {filtered.map(t => (
          <Paper
            key={t.id}
            sx={{
              px: 3, py: 2.5, background: "rgba(255,255,255,0.90)",
              borderLeft: `6px solid ${priorityColor[t.priority]}`,
              borderRadius: 3, boxShadow: "0 2px 12px rgba(60,60,120,0.09)"
            }}
          >
            <Stack direction="row" alignItems="flex-start" justifyContent="space-between">
              <Box sx={{ flex: 1 }}>
                <Typography variant="h6" fontWeight={700} sx={{ mb: 0.5 }}>
                  <InlineEdit
                    value={t.title}
                    onSave={val => updateTask(t.id, "title", val)}
                    style={{ fontWeight: 700, fontSize: "1.13rem" }}
                  />
                </Typography>
                <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 1 }}>
                  <FormControl size="small" sx={{ minWidth: 120 }}>
                    <InputLabel>Statut</InputLabel>
                    <Select
                      value={t.status}
                      label="Statut"
                      onChange={e => updateTask(t.id, "status", e.target.value)}
                    >
                      {statusOptions.map(status =>
                        <MenuItem key={status} value={status}>{status}</MenuItem>
                      )}
                    </Select>
                  </FormControl>
                  <FormControl size="small" sx={{ minWidth: 120 }}>
                    <InputLabel>Priorité</InputLabel>
                    <Select
                      value={t.priority}
                      label="Priorité"
                      onChange={e => updateTask(t.id, "priority", e.target.value)}
                      sx={{
                        color: priorityColor[t.priority], fontWeight: "bold"
                      }}
                    >
                      <MenuItem value="Haute" sx={{ color: priorityColor["Haute"] }}>Haute</MenuItem>
                      <MenuItem value="Moyenne" sx={{ color: priorityColor["Moyenne"] }}>Moyenne</MenuItem>
                      <MenuItem value="Basse" sx={{ color: priorityColor["Basse"] }}>Basse</MenuItem>
                    </Select>
                  </FormControl>
                  <FormControl size="small" sx={{ minWidth: 120 }}>
                    <InputLabel>Assigné à</InputLabel>
                    <Select
                      value={t.assignedTo || ""}
                      label="Assigné à"
                      onChange={e => updateTask(t.id, "assignedTo", e.target.value ? Number(e.target.value) : null)}
                    >
                      <MenuItem value="">Non assigné</MenuItem>
                      {users.map(u =>
                        <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
                      )}
                    </Select>
                  </FormControl>
                </Stack>
                <Typography
                  variant="body2"
                  color="text.secondary"
                  sx={{ fontWeight: 500, mb: 0.2 }}
                >Notes :</Typography>
                <InlineEdit
                  value={t.notes}
                  onSave={val => updateTask(t.id, "notes", val)}
                  type="textarea"
                  style={{
                    width: "100%", minHeight: 32, background: "#f7fafd",
                    borderRadius: 4, color: "#111", fontSize: "1rem"
                  }}
                />
              </Box>
              <Box>
                <Tooltip title="Supprimer">
                  <IconButton
                    aria-label="Supprimer"
                    color="error"
                    onClick={() => removeTask(t.id)}
                    sx={{ ml: 2, width: 44, height: 44 }}
                  >
                    <DeleteIcon fontSize="medium" />
                  </IconButton>
                </Tooltip>
              </Box>
            </Stack>
          </Paper>
        ))}
        {!filtered.length && <Typography sx={{ color: "#888", mt: 3 }}>Aucune tâche trouvée…</Typography>}
      </Stack>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={1800}
        onClose={() => setSnackbar({ open: false, msg: "" })}
        message={snackbar.msg}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
      />
    </Box>
  );
}
