import React, { useState, useCallback, useEffect } from "react";
import axios from "axios";
import {
  ThemeProvider,
  CssBaseline,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Button,
  Container,
  Paper,
  Tabs,
  Tab,
  Snackbar,
  Stack,
} from "@mui/material";
import ListAltIcon from "@mui/icons-material/ListAlt";
import PeopleAltIcon from "@mui/icons-material/PeopleAlt";
import SaveAltIcon from "@mui/icons-material/SaveAlt";
import CloudDownloadIcon from "@mui/icons-material/CloudDownload";
import LogoutIcon from "@mui/icons-material/Logout";
import ThemeOfTheMonth from "./components/ThemeOfTheMonth";
import TaskBoard from "./components/TaskBoard";
import ContactBoard from "./components/ContactBoard";
import ProgressDashboard from "./components/ProgressDashboard";
import theme from "./theme";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

function getToken() {
  return localStorage.getItem("token");
}
function setToken(token) {
  localStorage.setItem("token", token);
}
function removeToken() {
  localStorage.removeItem("token");
}
function authHeaders() {
  return { Authorization: "Bearer " + getToken() };
}

function AuthForm({ onLogin }) {
  const [mode, setMode] = useState("login");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");
  async function handleSubmit(e) {
    e.preventDefault();
    setErr("");
    try {
      if (mode === "register") {
        await axios.post("http://localhost:4000/register", {
          username,
          password,
        });
        setMode("login");
        setErr("Compte créé. Connectez-vous !");
      } else {
        const res = await axios.post("http://localhost:4000/login", {
          username,
          password,
        });
        setToken(res.data.token);
        await onLogin();
      }
    } catch (e) {
      setErr(e?.response?.data?.error || "Erreur serveur");
    }
  }
  return (
    <Container
      maxWidth="xs"
      sx={{ minHeight: "100vh", display: "flex", alignItems: "center" }}
    >
      <Paper sx={{ width: "100%", p: 4, borderRadius: 4 }}>
        <Typography variant="h5" fontWeight={700} align="center" mb={2}>
          {mode === "login" ? "Connexion" : "Inscription"}
        </Typography>
        <form onSubmit={handleSubmit}>
          <input
            placeholder="Nom d'utilisateur"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={{
              width: "100%",
              marginBottom: 12,
              padding: 10,
              fontSize: 16,
              borderRadius: 6,
              border: "1px solid #eaeaea",
            }}
          />
          <input
            placeholder="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            type="password"
            style={{
              width: "100%",
              marginBottom: 12,
              padding: 10,
              fontSize: 16,
              borderRadius: 6,
              border: "1px solid #eaeaea",
            }}
          />
          <Button
            variant="contained"
            color="primary"
            type="submit"
            fullWidth
            sx={{ mb: 1, fontWeight: 600 }}
          >
            {mode === "login" ? "Se connecter" : "Créer un compte"}
          </Button>
        </form>
        <Button
          color="secondary"
          fullWidth
          size="small"
          onClick={() => setMode(mode === "login" ? "register" : "login")}
        >
          {mode === "login" ? "Créer un compte" : "Déjà inscrit ? Se connecter"}
        </Button>
        {err && (
          <Typography color="error" align="center" sx={{ mt: 2 }}>
            {err}
          </Typography>
        )}
      </Paper>
    </Container>
  );
}

function exportPDF(tasks, contacts) {
  const doc = new jsPDF();

  // Titre général
  doc.setFontSize(18);
  doc.text("Dashboard Collaboratif – Export PDF", 14, 15);

  // --- Table tâches
  doc.setFontSize(15);
  doc.text("Tâches", 14, 30);

  autoTable(doc, {
    startY: 35,
    head: [["Titre", "Statut", "Priorité", "Assigné à", "Notes"]],
    body: tasks.map(t => [
      t.title,
      t.status,
      t.priority,
      t.assignedTo || "",
      (t.notes || "").substring(0, 50)
    ]),
    styles: { fontSize: 10 },
  });

  let currentY = doc.previousAutoTable.finalY + 10;

  // --- Table contacts
  doc.setFontSize(15);
  doc.text("Contacts", 14, currentY);

  autoTable(doc, {
    startY: currentY + 5,
    head: [["Nom", "Type", "Statut", "Priorité", "Notes"]],
    body: contacts.map(c => [
      c.name,
      c.type,
      c.status,
      c.priority,
      (c.notes || "").substring(0, 50)
    ]),
    styles: { fontSize: 10 },
  });

  doc.save("dashboard-export.pdf");
}


export default function App() {
  const [user, setUser] = useState(null);
  const [page, setPage] = useState("loading");
  const [tasks, setTasks] = useState([]);
  const [contacts, setContacts] = useState([]);
  const [users, setUsers] = useState([]);
  const [githubStatus, setGithubStatus] = useState("");
  const [snack, setSnack] = useState("");
  const [tab, setTab] = useState(0);

  // Nouvelle variable pour forcer le refresh du thème après édition
  const [themeKey, setThemeKey] = useState(0);

  // Load everything
  const loadUserAndData = useCallback(async () => {
    const token = getToken();
    if (!token) {
      setUser(null);
      setPage("auth");
      setTasks([]);
      setContacts([]);
      setUsers([]);
      return;
    }
    try {
      const me = await axios.get("http://localhost:4000/me", {
        headers: authHeaders(),
      });
      setUser(me.data);
      setPage("dashboard");
      const [tasksRes, contactsRes, usersRes] = await Promise.all([
        axios.get("http://localhost:4000/tasks", { headers: authHeaders() }),
        axios.get("http://localhost:4000/contacts", { headers: authHeaders() }),
        axios.get("http://localhost:4000/users", { headers: authHeaders() }),
      ]);
      setTasks(tasksRes.data);
      setContacts(contactsRes.data);
      setUsers(usersRes.data);
    } catch (e) {
      removeToken();
      setUser(null);
      setPage("auth");
      setTasks([]);
      setContacts([]);
      setUsers([]);
    }
  }, []);
  useEffect(() => {
    loadUserAndData();
  }, [loadUserAndData]);

  // CRUD
  const fetchTasks = async () => {
    const res = await axios.get("http://localhost:4000/tasks", {
      headers: authHeaders(),
    });
    setTasks(res.data);
  };
  const fetchContacts = async () => {
    const res = await axios.get("http://localhost:4000/contacts", {
      headers: authHeaders(),
    });
    setContacts(res.data);
  };
  const handleTaskAdd = async (task) => {
    await axios.post("http://localhost:4000/tasks", task, {
      headers: authHeaders(),
    });
    fetchTasks();
    setSnack("Tâche ajoutée !");
  };
  const handleTaskUpdate = async (id, update) => {
    await axios.put(`http://localhost:4000/tasks/${id}`, update, {
      headers: authHeaders(),
    });
    fetchTasks();
    setSnack("Tâche modifiée");
  };
  const handleTaskDelete = async (id) => {
    await axios.delete(`http://localhost:4000/tasks/${id}`, {
      headers: authHeaders(),
    });
    fetchTasks();
    setSnack("Tâche supprimée");
  };
  const handleContactAdd = async (contact) => {
    await axios.post("http://localhost:4000/contacts", contact, {
      headers: authHeaders(),
    });
    fetchContacts();
    setSnack("Contact ajouté !");
  };
  const handleContactUpdate = async (id, update) => {
    await axios.put(`http://localhost:4000/contacts/${id}`, update, {
      headers: authHeaders(),
    });
    fetchContacts();
    setSnack("Contact modifié");
  };
  const handleContactDelete = async (id) => {
    await axios.delete(`http://localhost:4000/contacts/${id}`, {
      headers: authHeaders(),
    });
    fetchContacts();
    setSnack("Contact supprimé");
  };

  async function saveToGitHub(setStatus) {
    setStatus("Envoi sur GitHub...");
    try {
      const res = await axios.post("http://localhost:4000/github-save", {
        tasks,
        contacts,
      });
      setStatus(
        res.data.ok
          ? "Sauvegardé sur GitHub !"
          : "Erreur : fallback issue GitHub créé"
      );
    } catch {
      setStatus("Erreur critique lors de la sauvegarde");
    }
  }
  function exportJSON() {
    const blob = new Blob([JSON.stringify({ tasks, contacts }, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "dashboard-export.json";
    a.click();
    URL.revokeObjectURL(url);
  }
  function logout() {
    removeToken();
    setUser(null);
    setPage("auth");
    setTasks([]);
    setContacts([]);
    setUsers([]);
  }

  // Handler pour que ThemeOfTheMonth puisse notifier App d'une modification
  const handleThemeChanged = () => setThemeKey((k) => k + 1);

  if (page === "loading") return <div>Chargement…</div>;
  if (page === "auth") return <AuthForm onLogin={loadUserAndData} />;

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: "100vh",
          background: "linear-gradient(120deg, #2979ff 0%, #8f5cff 100%)",
          pb: 8,
        }}
      >
        <AppBar
          position="static"
          color="primary"
          elevation={0}
          sx={{ background: "rgba(41,121,255,0.93)" }}
        >
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 700 }}>
              Dashboard Collaboratif
            </Typography>
            <Typography sx={{ mr: 2 }}>
              Connecté en tant que <b>{user?.username}</b>
            </Typography>
            <Button color="inherit" startIcon={<LogoutIcon />} onClick={logout}>
              Se déconnecter
            </Button>
          </Toolbar>
        </AppBar>
        <Container maxWidth="md" sx={{ mt: 4 }}>
          {/* Thème du mois (éditable pour admin) */}
          <ThemeOfTheMonth
            key={themeKey}
            user={user}
            onThemeChanged={handleThemeChanged}
          />

          {/* Dashboard/Stats (glasscard) */}
          <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
            <ProgressDashboard tasks={tasks} />
          </Paper>

          {/* Tabs Navigation */}
          <Tabs
            value={tab}
            onChange={(_, v) => setTab(v)}
            variant="fullWidth"
            sx={{
              mb: 3,
              borderRadius: 2,
              boxShadow: "0 2px 12px rgba(80,80,120,0.13)",
              background: "rgba(255,255,255,0.94)",
            }}
          >
            <Tab
              icon={<ListAltIcon />}
              iconPosition="start"
              label="Tâches"
              sx={{ fontWeight: 700 }}
            />
            <Tab
              icon={<PeopleAltIcon />}
              iconPosition="start"
              label="Contacts"
              sx={{ fontWeight: 700 }}
            />
          </Tabs>

          {/* Action buttons */}
          <Stack direction="row" spacing={2} mb={4} justifyContent="flex-end">
            <Button
              variant="contained"
              startIcon={<SaveAltIcon />}
              onClick={() => saveToGitHub(setGithubStatus)}
              sx={{
                background: "linear-gradient(90deg, #2979ff 0%, #8f5cff 100%)",
                color: "#fff",
                boxShadow: "0 4px 18px 0 rgba(41,121,255,0.10)",
                fontWeight: 600,
                textTransform: "none",
                borderRadius: 2,
                px: 3,
                "&:hover": {
                  background:
                    "linear-gradient(90deg, #2763e1 0%, #6c40bb 100%)",
                },
              }}
            >
              Sauvegarder sur GitHub
            </Button>
            <Button
              variant="contained"
              startIcon={<CloudDownloadIcon />}
              onClick={exportJSON}
              sx={{
                background: "#fff",
                color: "#6c40bb",
                fontWeight: 700,
                border: "2px solid #8f5cff",
                boxShadow: "0 2px 8px 0 rgba(41,121,255,0.08)",
                textTransform: "none",
                borderRadius: 2,
                px: 3,
                "&:hover": {
                  background: "#f3f0fb",
                },
              }}
            >
              Exporter en JSON
            </Button>
            <Typography sx={{ ml: 2, color: "#888", alignSelf: "center" }}>
              {githubStatus}
            </Typography>
          </Stack>

          {/* Onglets */}
          {tab === 0 && (
            <TaskBoard
              tasks={tasks}
              users={users}
              onAdd={handleTaskAdd}
              onUpdate={handleTaskUpdate}
              onDelete={handleTaskDelete}
            />
          )}
          {tab === 1 && (
            <ContactBoard
              contacts={contacts}
              onAdd={handleContactAdd}
              onUpdate={handleContactUpdate}
              onDelete={handleContactDelete}
            />
          )}
        </Container>
        <Snackbar
          open={!!snack}
          autoHideDuration={2200}
          onClose={() => setSnack("")}
          message={snack}
        />
      </Box>
    </ThemeProvider>
  );
}
