import React, { useState } from "react";
import {
  Button, List, ListItem, ListItemText, Select, MenuItem, Box, TextField, Dialog,
  DialogActions, DialogContent, DialogTitle, Snackbar, IconButton, Typography
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const priorityColor = { 'Haute': 'red', 'Moyenne': 'orange', 'Basse': 'green' };

export default function TaskBoard({ tasks, users, onAdd, onUpdate, onDelete }) {
  const [search, setSearch] = useState('');
  const [editing, setEditing] = useState(null); // {id, ...}
  const [dialogOpen, setDialogOpen] = useState(false);

  // Dialogue d'ajout/édition
  const handleDialogOpen = (task) => { setEditing(task || {}); setDialogOpen(true); };
  const handleDialogClose = () => { setEditing(null); setDialogOpen(false); };
  const handleDialogSave = () => {
    if (editing.id) onUpdate(editing.id, editing);
    else onAdd(editing);
    handleDialogClose();
  };

  // Recherche
  const filtered = tasks.filter(t =>
    t.title?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box sx={{ mb: 6 }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Tâches</Typography>
      <Box sx={{ display: "flex", mb: 2 }}>
        <TextField
          label="Rechercher une tâche"
          variant="outlined"
          value={search}
          onChange={e => setSearch(e.target.value)}
          sx={{ flex: 1, mr: 2 }}
        />
        <Button variant="contained" onClick={() => handleDialogOpen(null)}>Ajouter une tâche</Button>
      </Box>
      <List>
        {filtered.length === 0 ? <ListItem><ListItemText primary="Aucune tâche" /></ListItem> :
          filtered.map(t => (
            <ListItem
              key={t.id}
              sx={{
                border: "1px solid #ddd",
                borderRadius: 2,
                mb: 1,
                bgcolor: "#fff",
                boxShadow: 1
              }}
              secondaryAction={
                <IconButton edge="end" color="error" onClick={() => onDelete(t.id)}>
                  <DeleteIcon />
                </IconButton>
              }
              onClick={() => handleDialogOpen(t)}
            >
              <ListItemText
                primary={
                  <Box>
                    <b>{t.title}</b>{" "}
                    <span style={{ color: priorityColor[t.priority], fontWeight: "bold" }}>{t.priority}</span>
                    <span style={{ marginLeft: 8, fontStyle: 'italic', color: "#555" }}>
                      {t.status}
                    </span>
                  </Box>
                }
                secondary={
                  <>
                    <div>
                      Assignée à : <b>{users.find(u => u.id === t.assignedTo)?.username || "—"}</b>
                    </div>
                    {t.notes && <div style={{ color: "#777" }}>Notes : {t.notes}</div>}
                  </>
                }
              />
            </ListItem>
          ))}
      </List>
      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle>{editing?.id ? "Modifier la tâche" : "Nouvelle tâche"}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Titre"
            fullWidth
            value={editing?.title || ''}
            onChange={e => setEditing(edit => ({ ...edit, title: e.target.value }))}
          />
          <TextField
            margin="dense"
            label="Notes"
            fullWidth
            value={editing?.notes || ''}
            onChange={e => setEditing(edit => ({ ...edit, notes: e.target.value }))}
          />
          <Select
            label="Statut"
            fullWidth
            value={editing?.status || 'À faire'}
            onChange={e => setEditing(edit => ({ ...edit, status: e.target.value }))}
            sx={{ mt: 2 }}
          >
            <MenuItem value="À faire">À faire</MenuItem>
            <MenuItem value="En cours">En cours</MenuItem>
            <MenuItem value="En attente">En attente</MenuItem>
            <MenuItem value="Terminé">Terminé</MenuItem>
          </Select>
          <Select
            label="Priorité"
            fullWidth
            value={editing?.priority || 'Moyenne'}
            onChange={e => setEditing(edit => ({ ...edit, priority: e.target.value }))}
            sx={{ mt: 2 }}
          >
            <MenuItem value="Haute">Haute</MenuItem>
            <MenuItem value="Moyenne">Moyenne</MenuItem>
            <MenuItem value="Basse">Basse</MenuItem>
          </Select>
          <Select
            label="Assigner à"
            fullWidth
            value={editing?.assignedTo || ''}
            onChange={e => setEditing(edit => ({ ...edit, assignedTo: e.target.value }))}
            sx={{ mt: 2 }}
          >
            <MenuItem value="">—</MenuItem>
            {users.map(u => (
              <MenuItem key={u.id} value={u.id}>{u.username}</MenuItem>
            ))}
          </Select>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose}>Annuler</Button>
          <Button variant="contained" onClick={handleDialogSave}>
            {editing?.id ? "Enregistrer" : "Ajouter"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
