import React from "react";

const priorityColor = { Haute: "red", Moyenne: "orange", Basse: "green" };

function ContactBoard({ contacts, onUpdate }) {
  const updateContact = (id, field, value) => {
    onUpdate(contacts.map((c) => (c.id === id ? { ...c, [field]: value } : c)));
  };

  const addContact = async () => {
    const name = prompt("Nom du contact ?");
    if (name) {
      // Construction du contact à envoyer
      const newContact = {
        id: Date.now(), // id généré côté front pour l’instant
        name,
        type: "Organisation",
        priority: "Basse",
        status: "À contacter",
        notes: "",
      };

      // --- Nouvelle partie : appel au backend
      try {
        const resp = await fetch("http://localhost:4000/contacts", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(newContact),
        });
        const saved = await resp.json();
        // Utilise la réponse du backend (par ex. avec un vrai id)
        onUpdate([...contacts, saved]);
      } catch (e) {
        alert("Erreur lors de l'ajout du contact");
      }
    }
  };

  const removeContact = (id) => {
    if (window.confirm("Supprimer ce contact ?")) {
      onUpdate(contacts.filter((c) => c.id !== id));
    }
  };

  return (
    <div>
      <h2>Contacts</h2>
      <button onClick={addContact}>Ajouter un contact</button>
      <ul>
        {contacts.map((c) => (
          <li
            key={c.id}
            style={{ border: "1px solid #ddd", margin: 8, padding: 8 }}
          >
            <b>{c.name}</b> ({c.type})
            <span
              style={{
                color: priorityColor[c.priority],
                fontWeight: "bold",
                marginLeft: 10,
              }}
            >
              {c.priority}
            </span>
            <br />
            Statut :
            <select
              value={c.status}
              onChange={(e) => updateContact(c.id, "status", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>À contacter</option>
              <option>En contact</option>
              <option>En attente</option>
              <option>Terminé</option>
            </select>
            <br />
            Priorité :
            <select
              value={c.priority}
              onChange={(e) => updateContact(c.id, "priority", e.target.value)}
              style={{ marginLeft: 5 }}
            >
              <option>Haute</option>
              <option>Moyenne</option>
              <option>Basse</option>
            </select>
            <br />
            Notes :
            <textarea
              value={c.notes}
              onChange={(e) => updateContact(c.id, "notes", e.target.value)}
              style={{ width: "95%", minHeight: 30 }}
            />
            <button
              onClick={() => removeContact(c.id)}
              style={{ color: "red" }}
            >
              Supprimer
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ContactBoard;
