import React from "react";
import { Box, Typography, LinearProgress, Chip } from "@mui/material";

// --- Données du thème (remplace par une requête API si besoin plus tard)
const theme = {
  title: "Les conflits vus par les opprimés",
  description:
    "Ce mois-ci, nous donnons la parole à celles et ceux qui vivent les conflits au quotidien. Notre but : mieux comprendre les réalités, et porter une voix authentique pour la paix.",
  objectives: [
    { label: "Contacter 5 associations/ONG", done: 3 },
    { label: "Recueillir 3 témoignages directs", done: 1 },
    { label: "Programmer 2 interviews vidéo", done: 1 },
  ],
  quote:
    "« On ne peut pas construire la paix sans écouter ceux qui vivent la guerre. »",
};

function ThemeOfTheMonth() {
  // Calcul progression globale
  const total = theme.objectives.length;
  const sumDone = theme.objectives.reduce((acc, o) => acc + (o.done > 0 ? 1 : 0), 0);
  const percent = Math.round((sumDone / total) * 100);

  return (
    <Box
      sx={{
        background: "linear-gradient(90deg, #ede7f6 0%, #fff 100%)",
        borderRadius: 3,
        p: 3,
        mb: 3,
        boxShadow: 1,
        minWidth: 320,
        maxWidth: 700,
        margin: "20px auto 0 auto",
      }}
    >
      <Chip
        label="THÈME DU MOIS"
        color="secondary"
        sx={{ fontWeight: "bold", mb: 1, background: "#6a1b9a", color: "white" }}
      />
      <Typography variant="h5" fontWeight="bold" gutterBottom color="primary">
        {theme.title}
      </Typography>
      <Typography variant="body1" sx={{ mb: 2 }}>
        {theme.description}
      </Typography>

      <Typography variant="subtitle1" fontWeight="bold">
        Objectifs :
      </Typography>
      <ul style={{ paddingLeft: 24, marginTop: 6, marginBottom: 10 }}>
        {theme.objectives.map((o, i) => (
          <li key={i} style={{ color: o.done > 0 ? "green" : "#444" }}>
            <span style={{ fontWeight: o.done > 0 ? "bold" : "normal" }}>
              {o.label}
            </span>
            {o.done > 0 && (
              <span style={{ marginLeft: 8, color: "green", fontSize: "1.2em" }}>
                ✓ ({o.done})
              </span>
            )}
          </li>
        ))}
      </ul>
      <LinearProgress
        variant="determinate"
        value={percent}
        sx={{ height: 8, borderRadius: 4, mb: 1, background: "#d1c4e9" }}
        color="secondary"
      />
      <Typography variant="caption" color="textSecondary">
        Progression globale : {percent}%
      </Typography>

      {theme.quote && (
        <Box sx={{ mt: 2, p: 2, background: "#f8bbd0", borderRadius: 2 }}>
          <Typography variant="body2" fontStyle="italic" color="secondary">
            {theme.quote}
          </Typography>
        </Box>
      )}
    </Box>
  );
}

export default ThemeOfTheMonth;
